CREATE OR REPLACE PROCEDURE gerar_evento_pendencia_conta ( nr_atendimento_p bigint, ie_evento_p text, cd_estabelecimento_p bigint, nm_usuario_p text, nr_chave_n1_p bigint, nr_chave_n2_p bigint, nr_chave_n3_p bigint, nr_chave_n4_p bigint, nr_chave_n5_p bigint, nr_chave_n6_p bigint, nr_chave_n7_p bigint, nr_chave_n8_p bigint, nr_chave_v1_p text, nr_chave_v2_p text, nr_chave_v3_p text, nr_chave_v4_p text, nr_chave_v5_p text, nr_chave_d1_p timestamp, nr_chave_d2_p timestamp, nr_chave_d3_p timestamp, ie_commit_p text) AS $body$
DECLARE



qt_existe_w				bigint;
nr_seq_tipo_w			bigint;
nr_seq_estagio_w		bigint;
dt_pendencia_w  		timestamp;
nr_seq_regra_resp_w		bigint;
nr_seq_regra_item_w		bigint;
nr_seq_pendencia_w		bigint;
nr_interno_conta_w		bigint;
cd_setor_atendimento_w	bigint;
cd_convenio_w			integer;

C01 CURSOR FOR
	SELECT	nr_seq_tipo
	from	regra_geracao_pend_conta
	where	ie_situacao = 'A'
	and 	cd_estabelecimento = cd_estabelecimento_p
	and 	coalesce(cd_convenio, coalesce(cd_convenio_w,0)) = coalesce(cd_convenio_w,0)
	and 	ie_evento = ie_evento_p
	order by ie_evento,
			coalesce(cd_convenio,0);


BEGIN

nr_interno_conta_w:= null;
dt_pendencia_w	  := clock_timestamp();

select	count(*)
into STRICT	qt_existe_w
from	regra_geracao_pend_conta
where 	cd_estabelecimento = cd_estabelecimento_p;

if (qt_existe_w > 0) then

	cd_convenio_w:= obter_convenio_atendimento(nr_atendimento_p);

	open C01;
		loop
		fetch C01 into
			nr_seq_tipo_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			nr_seq_tipo_w			:= nr_seq_tipo_w;

			end;
		end loop;
	close C01;

	select  min(nr_sequencia)
    into STRICT    nr_seq_estagio_w
    from    cta_estagio_pend
    where   coalesce(ie_situacao,'A') = 'A'
    and     ie_tipo_estagio = 'A'
    and	  	cta_obter_se_estagio_lib(nr_sequencia, obter_perfil_ativo, nm_usuario_p, nr_seq_tipo_w) = 'S';

	SELECT * FROM cta_obter_regra_resp_pend(nr_seq_tipo_w, nr_seq_estagio_w, dt_pendencia_w, nr_seq_regra_resp_w, nr_seq_regra_item_w) INTO STRICT nr_seq_regra_resp_w, nr_seq_regra_item_w;

	cd_setor_atendimento_w:= obter_setor_atendimento(nr_atendimento_p);

	if (coalesce(nr_seq_regra_resp_w,0) <> 0) then

			select  nextval('cta_pendencia_seq')
			into STRICT    nr_seq_pendencia_w
			;

			insert into cta_pendencia(
					nr_sequencia,
					nr_atendimento,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					dt_pendencia,
					nr_seq_tipo,
					nr_seq_estagio,
					nr_interno_conta,
					cd_setor_atendimento,
					nr_seq_regra_resp,
					ds_complemento
			) values (
					nr_seq_pendencia_w,
					nr_atendimento_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					dt_pendencia_w,
					nr_seq_tipo_w,
					nr_seq_estagio_w,
					nr_interno_conta_w,
					cd_setor_atendimento_w,
					nr_seq_regra_resp_w,
					OBTER_DESC_EXPRESSAO(729104));				-- 729104: 'Gerado pela regra geração pendências da conta'
	end if;

end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
