CREATE OR REPLACE PROCEDURE gerar_evento_prevista_alta ( cd_estabelecimento_p bigint, nr_atendimento_p bigint, ie_probabilidade_alta_p text, nm_usuario_p text) AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(10);
nr_seq_evento_w			bigint;

qt_idade_w			bigint;
						
						 
c04 CURSOR FOR 
	SELECT	a.nr_seq_evento 
	from	regra_envio_sms a 
	where	a.cd_estabelecimento	= cd_estabelecimento_p 
	and	a.ie_evento_disp	= 'ALP' 
	and	((ie_probabilidade_alta = ie_probabilidade_alta_p) or (coalesce(ie_probabilidade_alta::text, '') = '')) 
	and	qt_idade_w between coalesce(qt_idade_min,0)	and coalesce(qt_idade_max,9999) 
	and	coalesce(a.ie_situacao,'A') = 'A';
			

BEGIN 
 
select	max(cd_pessoa_fisica) 
into STRICT 	cd_pessoa_fisica_w 
from 	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
qt_idade_w	:= coalesce(obter_idade_pf(cd_pessoa_fisica_w,clock_timestamp(),'A'),0);
open c04;
	loop 
	fetch c04 into	 
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on c04 */
		begin 
		CALL gerar_evento_paciente_trigger(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_w,null,nm_usuario_p);
		end;
	end loop;
	close c04;
 
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
