CREATE OR REPLACE PROCEDURE gerar_evento_reinternacao ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

nr_seq_evento_w	bigint;
qt_reg_w		bigint;
qt_idade_w			bigint;

C01 CURSOR FOR 
	SELECT	nr_seq_evento 
	from	regra_envio_sms 
	where	cd_estabelecimento	= cd_estabelecimento_p 
	and	ie_evento_disp = 'R' 
	and	qt_idade_w between coalesce(qt_idade_min,0)	and coalesce(qt_idade_max,9999) 
	and	coalesce(ie_situacao,'A') = 'A';


BEGIN 
 
select	count(*) 
into STRICT	qt_reg_w 
from	atendimento_paciente 
where	cd_pessoa_fisica = cd_pessoa_fisica_p 
and	nr_atendimento <> nr_atendimento_p 
and	ie_tipo_atendimento = 1 
and	dt_alta >= (clock_timestamp() - interval '30 days');
qt_idade_w	:= coalesce(obter_idade_pf(cd_pessoa_fisica_p,clock_timestamp(),'A'),0);
if (qt_reg_w >= 1) then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL gerar_evento_paciente(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_p,null,nm_usuario_p,null);
		end;
	end loop;
	close C01;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
