SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_evento_reint_isol (nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

nr_seq_evento_w		bigint;
qt_idade_w		bigint;
qt_dias_isolamento_w	bigint;
qt_isolamento_w		bigint;
cd_setor_paciente_w	bigint;

C01 CURSOR FOR 
	SELECT	nr_seq_evento 
	from	regra_envio_sms 
	where	cd_estabelecimento	= cd_estabelecimento_p 
	and	ie_evento_disp = 'RIS' 
	and	qt_idade_w between coalesce(qt_idade_min,0)	and coalesce(qt_idade_max,9999) 
	and	coalesce(cd_setor_atendimento,cd_setor_paciente_w) = cd_setor_paciente_w 
	and	coalesce(ie_situacao,'A') = 'A';


BEGIN 
 
qt_dias_isolamento_w := obter_param_usuario(916, 400, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, qt_dias_isolamento_w);
cd_setor_paciente_w	:= coalesce(obter_setor_atendimento(nr_atendimento_p),0);
 
select 	count(*) 
into STRICT	qt_isolamento_w 
from	motivo_isolamento a, 
	motivo_isolamento_atend b, 
	atendimento_paciente c 
where	a.nr_sequencia = b.nr_seq_motivo_isol 
and	c.nr_atendimento = b.nr_atendimento 
and	c.cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	coalesce(a.ie_gerar_alerta,'N') = 'S' 
and (clock_timestamp() - b.dt_inicio_isolamento ) <= qt_dias_isolamento_w;
 
if (coalesce(qt_isolamento_w,0) = 0) then 
	select	count(*) 
	into STRICT	qt_isolamento_w 
	from	atendimento_paciente a, 
			atendimento_precaucao b, 
			motivo_isolamento c 
	where	a.nr_atendimento = b.nr_atendimento 
	and		a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and		c.nr_sequencia = b.nr_seq_motivo_isol 
	and		exists (SELECT	1 
					from	cih_precaucao x 
					where	x.nr_sequencia = b.nr_seq_precaucao 
					and		coalesce(x.ie_isolamento,'N') = 'S') 
	and		coalesce(c.ie_gerar_alerta,'N') = 'S' 
	and (clock_timestamp() - b.dt_inicio) <= qt_dias_isolamento_w 
	and		coalesce(b.DT_INATIVACAO::text, '') = '';
end if;
 
qt_idade_w	:= coalesce(obter_idade_pf(cd_pessoa_fisica_p,clock_timestamp(),'A'),0);
 
if (coalesce(qt_isolamento_w,0) > 0) then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL gerar_evento_paciente(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_p,null,nm_usuario_p,null);
		end;
	end loop;
	close C01;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
