CREATE OR REPLACE PROCEDURE gerar_evento_result_sae ( nr_atendimento_p bigint, nr_seq_evento_p bigint, cd_setor_atendimento_p bigint, ds_evento_p text, nm_usuario_p text, cd_pessoa_fisica_p text, ie_liberar_p text default 'N', nr_seq_sae_p bigint default null, nr_seq_item_p bigint default null, nr_seq_result_p bigint default null) AS $body$
DECLARE


cd_estabelecimento_w	bigint;
nr_sequencia_w			bigint;
cd_profissional_w		varchar(15);
dt_liberacao_w			timestamp := null;
ie_liberar_w			varchar(5);
ds_evento_w				varchar(4000);
ds_item_w				varchar(255);
ds_result_w				varchar(255);
ds_mensagem_w			varchar(4000);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_evento_p IS NOT NULL AND nr_seq_evento_p::text <> '') and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') and (ds_evento_p IS NOT NULL AND ds_evento_p::text <> '') then
	
	ie_liberar_w := ie_liberar_p;
	
	cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
	
	if (ie_liberar_w = 'S') then		
		dt_liberacao_w := clock_timestamp();
	end if;
	
	cd_profissional_w :=  Obter_Dados_Usuario_Opcao(nm_usuario_p,'C');
	
	ds_mensagem_w := substr(obter_texto_tasy(263704, wheb_usuario_pck.get_nr_seq_idioma),1,4000);
	
	if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (nr_seq_result_p IS NOT NULL AND nr_seq_result_p::text <> '') then
		begin			
		ds_item_w := substr(obter_desc_item_examinar(nr_seq_item_p),1,100);
		ds_result_w := substr(obter_desc_item_resultado(nr_seq_result_p),1,100);
		exception
			when others then
				ds_item_w	:= null;
				ds_result_w := null;
			end;	
		
	end if;
	
	If (ds_item_w IS NOT NULL AND ds_item_w::text <> '') then
		
		ds_mensagem_w := substr((ds_mensagem_w || chr(13) ||ds_item_w),1,4000);
		
		if (ds_result_w IS NOT NULL AND ds_result_w::text <> '') then
		
			ds_mensagem_w := substr((ds_mensagem_w || ' / ' ||ds_result_w),1,4000);
		
		end if;
		
	end if;
	
	if (ds_mensagem_w IS NOT NULL AND ds_mensagem_w::text <> '') then
		ds_evento_w := substr((ds_mensagem_w || chr(13) || chr(13) ||ds_evento_p),1,4000);
	else
		ds_evento_w := substr(ds_evento_p,1,4000);
	end if;
	
		Select  nextval('qua_evento_paciente_seq')
		into STRICT	nr_sequencia_w
		;
	
		insert into qua_evento_paciente(	nr_sequencia,
											nr_seq_evento,
											ds_evento,
											nr_atendimento,
											cd_pessoa_fisica,
											cd_profissional,
											nm_usuario,
											nm_usuario_origem,
											nm_usuario_nrec,
											nm_usuario_reg,
											cd_estabelecimento,
											cd_setor_atendimento,
											dt_atualizacao,
											dt_atualizacao_nrec,
											dt_cadastro,
											dt_evento,
											ie_situacao,
											dt_liberacao,
											nr_seq_sae,
                                            ie_status,
											ie_origem,
											cd_funcao_ativa)
								values (	nr_sequencia_w,
											nr_seq_evento_p,
											ds_evento_w,
											nr_atendimento_p,
											cd_pessoa_fisica_p,
											cd_profissional_w,
											nm_usuario_p,
											nm_usuario_p,
											nm_usuario_p,
											nm_usuario_p,
											cd_estabelecimento_w,
											cd_setor_atendimento_p,
											clock_timestamp(),
											clock_timestamp(),
											clock_timestamp(),
											clock_timestamp(),
											'A',
											dt_liberacao_w,
											nr_seq_sae_p,
                                            1,
											'S',
											obter_funcao_ativa);
	

	commit;	
	

	if (ie_liberar_w = 'S') then	
	
		CALL gerar_ficha_UPP_evento(nr_sequencia_w,nm_usuario_p);
	
	end if;
	
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
