CREATE OR REPLACE PROCEDURE gerar_evolucao_anamnese ( nr_atendimento_p bigint, nm_usuario_p text, ds_evolucao_p text, cd_pessoa_fisica_p text, cd_medico_p text, cd_especial_medico_p bigint, nr_sequencia_p bigint default null) AS $body$
DECLARE

 
ie_tipo_evolucao_w	  varchar(3);
ie_evolucao_clinica_W  varchar(3);
