BEGIN 
 
ie_tipo_evolucao_w 	:= Obter_Dados_Usuario_Opcao(nm_usuario_p,'F');
ie_evolucao_clinica_W := obter_param_usuario(281, 1285, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_evolucao_clinica_W);
 
select	nextval('evolucao_paciente_seq') 
into STRICT	cd_evolucao_w
;
 
insert into 	evolucao_paciente(cd_evolucao, 
		 dt_evolucao, 
		 ie_tipo_evolucao,		 
		 cd_pessoa_fisica, 
		 nr_atendimento,				 
		 ie_situacao, 
		 dt_atualizacao, 
		 nm_usuario, 
		 ds_evolucao, 
		 cd_medico, 
		 cd_especialidade_medico, 
		 dt_liberacao, 
    ie_evolucao_clinica) 
values ( 
		 cd_evolucao_w, 
		 clock_timestamp(), 
		 ie_tipo_evolucao_w,		 
		 cd_pessoa_fisica_p, 
		 nr_atendimento_p,		 
		 'A', 
		 clock_timestamp(), 
		 nm_usuario_p, 
		 ds_evolucao_p, 
		 cd_medico_p, 
		 cd_especial_medico_p, 
		 clock_timestamp(), 
    ie_evolucao_clinica_W);
 
commit;
 
if (nr_sequencia_p > -1) then 
	CALL gerar_evolucao_vinculo(nr_sequencia_p,cd_evolucao_w,'AN');
end if;
 
end;
