CREATE OR REPLACE PROCEDURE gerar_evolucao_atend_regulacao ( nr_seq_regulacao_p bigint, nr_atendimento_p bigint, cd_paciente_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_regulacao_w	bigint;

ie_verifica_evolucao_w  varchar(1);
ie_tipo_evolucao_w	varchar(3);
cd_profissional_w	varchar(10);


C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	eme_reg_evolucao a,
		eme_regulacao b
	where	a.nr_seq_reg = b.nr_sequencia
	and	b.nr_atendimento = coalesce(nr_atendimento_p,0)
	and     not exists ( 	SELECT 1
                   		from   evolucao_paciente x
                   		where  a.nr_sequencia = x.nr_seq_regulacao)
	and	coalesce(a.dt_inativacao::text, '') = ''
	order by 1;


BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	max(ie_tipo_evolucao),
		max(cd_pessoa_fisica)
	into STRICT	ie_tipo_evolucao_w,
		cd_profissional_w
	from	usuario
	where	nm_usuario = nm_usuario_p;
end if;


open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL gerar_evolucao_hist_regulacao(nr_sequencia_w, ie_tipo_evolucao_w, nr_atendimento_p, cd_profissional_w, cd_paciente_p, nm_usuario_p);

	end;
end loop;
close C01;

if (nr_seq_regulacao_p IS NOT NULL AND nr_seq_regulacao_p::text <> '') then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_verifica_evolucao_w
	from   	evolucao_paciente x
	where  	x.cd_pessoa_fisica  = cd_paciente_p
	and	coalesce(x.nr_atendimento::text, '') = '';

	if (ie_verifica_evolucao_w = 'S') then

		update 	evolucao_paciente
		set	nr_atendimento	 = nr_atendimento_p
		where	cd_pessoa_fisica  = cd_paciente_p
		and	coalesce(nr_atendimento::text, '') = '';

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
