SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_evolucao_avaliacao_soap ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

										 
										 
										 
nr_seq_item_w		bigint;
ds_item_w		varchar(32000);
ds_resultado_w		varchar(32000);			
ds_conteudo_w		varchar(32000);		
ds_complemento_w		varchar(32000);
ie_resultado_w		varchar(10);
IE_EVOLUCAO_CLINICA_w	varchar(15);
ie_gerar_evolucao_w	varchar(10);		
nr_atendimento_w		bigint;
cd_pessoa_fisica_w	varchar(10);
cd_medico_w		varchar(10);
dt_avaliacao_w		timestamp;
cd_evolucao_w		bigint;
ds_evolucao_w		varchar(32000);
ie_tipo_evolucao_w		varchar(10);
ds_fonte_w		varchar(100);
ds_tam_fonte_w		varchar(10);
nr_tam_fonte_w		integer;
ds_cabecalho_w		varchar(32000);
ds_rodape_w		 varchar(32000);
ie_sem_resposta_w		varchar(1);
cd_dominio_w		bigint;
ds_titulo_w		varchar(100);
ds_enter_w		varchar(20)	:= '<p> </p> <p> </p>';
ds_titulo_rft_w varchar(255) := '';
ie_relev_resumo_alta_w varchar(1);
nr_cirurgia_w		bigint;
nr_seq_pepo_w		bigint;
nr_seq_soap_w		bigint;
ds_Funcao_Nutricao_w 	varchar(10);
cd_evolucao_r_w		bigint;

 
										 
C01 CURSOR FOR 
SELECT	c.nr_sequencia, 
		coalesce(ds_label_relat,c.ds_item) ds_item, 
		c.IE_RESULTADO, 
		coalesce((SELECT d.ds_resultado from med_item_avaliar_res d where d.nr_seq_item = c.nr_sequencia and obter_somente_numero(Aval(a.nr_sequencia,c.nr_sequencia)) = d.nr_Seq_res and coalesce(c.cd_dominio::text, '') = ''),substr(Aval(a.nr_sequencia,c.nr_sequencia),1,4000)) ds_resultado, 
		cd_dominio, 
		c.ds_complemento ds_complemento 
from	med_Avaliacao_paciente a, 
		med_tipo_avaliacao b, 
		med_item_avaliar c 
where	b.nr_sequencia = c.nr_seq_tipo 
and		coalesce(c.ie_situacao, 'A') = 'A' 
and		a.nr_seq_tipo_avaliacao = b.nr_sequencia 
and		((ie_sem_resposta_w = 'N') or 
		((c.ie_resultado in ('T','H')) and ( exists (select 1 
													 from  med_item_avaliar f 
													 where  f.nr_seq_superior = c.nr_sequencia 
													 and	  substr(Aval(a.nr_sequencia,f.nr_sequencia),1,4000) is not null))) or 
	(coalesce((select d.ds_resultado from med_item_avaliar_res d where d.nr_seq_item = c.nr_sequencia and obter_somente_numero(Aval(a.nr_sequencia,c.nr_sequencia)) = d.nr_Seq_res and coalesce(c.cd_dominio::text, '') = ''),substr(Aval(a.nr_sequencia,c.nr_sequencia),1,4000)) is not null)) 
and	((ie_sem_resposta_w = 'N') or 
 	((c.ie_resultado in ('T','H')) and ( exists (	select 1 
													from  med_item_avaliar f 
													where  f.nr_seq_superior = c.nr_sequencia 
													and	  substr(Aval(a.nr_sequencia,f.nr_sequencia),1,4000) <> 'N'))) or 
	(coalesce((select d.ds_resultado from med_item_avaliar_res d where d.nr_seq_item = c.nr_sequencia and obter_somente_numero(Aval(a.nr_sequencia,c.nr_sequencia)) = d.nr_Seq_res and coalesce(c.cd_dominio::text, '') = ''),substr(Aval(a.nr_sequencia,c.nr_sequencia),1,4000)) <> 'N')) 
and	a.nr_sequencia = nr_sequencia_p 
order by c.nr_seq_apresent;									
 
 
 
 

BEGIN 
 
select	max(ie_gerar_evolucao), 
	max(IE_EVOLUCAO_CLINICA), 
	max(cd_pessoa_fisica), 
	max(nr_atendimento), 
	max(cd_medico), 
	max(dt_avaliacao), 
	max(ds_tipo), 
	max(nr_cirurgia), 
	max(nr_seq_pepo), 
	max(nr_seq_soap) 
into STRICT	ie_gerar_evolucao_w, 
	IE_EVOLUCAO_CLINICA_w, 
	cd_pessoa_fisica_w, 
	nr_atendimento_w, 
	cd_medico_w, 
	dt_avaliacao_w, 
	ds_titulo_w, 
	nr_cirurgia_w, 
	nr_seq_pepo_w, 
  nr_seq_soap_w 
from	med_Avaliacao_paciente a, 
	med_tipo_avaliacao b 
where	b.nr_sequencia = a.nr_seq_tipo_avaliacao 
and	a.nr_sequencia = nr_sequencia_p;
 
 
if (IE_EVOLUCAO_CLINICA_w IS NOT NULL AND IE_EVOLUCAO_CLINICA_w::text <> '') and (ie_gerar_evolucao_w <> 'N') then 
 
	ds_Funcao_Nutricao_w := Obter_Param_Usuario(281, 174, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ds_Funcao_Nutricao_w);
	 
 
	select	coalesce(max(ie_campos_preench_evol_aval),'N') 
	into STRICT	ie_sem_resposta_w 
	from	parametro_medico 
	where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;
 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_item_w, 
		ds_item_w, 
		ie_resultado_w, 
		ds_resultado_w, 
		cd_dominio_w, 
		ds_complemento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin		 
		ds_resultado_w := replace(ds_resultado_w,chr(13),'<p> </p>');
		ds_resultado_w := replace(ds_resultado_w,chr(10),'<p> </p> ');
		 
		if (ie_resultado_w in ('X','T')) then 
			ds_conteudo_w	:= ds_conteudo_w||' <p> </p>'||'<p><strong>'||'<span style="text-decoration:underline;">' ||ds_item_w||'</span>' ||'</strong>'||'</p> <p> </p> <p> </p>';
			if ((trim(both ds_complemento_w) IS NOT NULL AND (trim(both ds_complemento_w))::text <> '')) and (ie_resultado_w in ('X')) then 
				ds_conteudo_w	:= ds_conteudo_w||' <p> </p>'||'<p><strong>'||'<span style="text-decoration:underline;">' ||ds_complemento_w||'</span>' ||'</strong></p>'||' <p> </p> <p> </p>';
			end if;
		elsif (ie_resultado_w = 'B') then 
			if (ds_resultado_w	= 'S') or (ds_resultado_w	= '1') then 
				ds_resultado_w	:= WHEB_MENSAGEM_PCK.get_texto(298977,null); --'Sim'; 
			else
				ds_resultado_w	:= WHEB_MENSAGEM_PCK.get_texto(298976,null); --'Não'; 
			end if;
			ds_conteudo_w := ds_conteudo_w ||' '||'<p><strong>'||ds_item_w||' : '||'</strong>'||ds_resultado_w||'</p><p> </p>';
		elsif (ie_resultado_w = 'O') then 
				if (cd_dominio_w > 0) then 
					select	max(ds_valor_dominio) 
					into STRICT	ds_resultado_w 
					from	med_valor_dominio 
					where	nr_seq_dominio = cd_dominio_w 
					and		vl_dominio = ds_resultado_w;
 
					ds_conteudo_w := ds_conteudo_w ||'<p> '||'<strong>'||ds_item_w||' : '||'</strong>'||ds_resultado_w||' </p><p> </p>';
				end if;
		elsif (ie_resultado_w in ('V','C')) then 
			ds_conteudo_w := ds_conteudo_w ||'<p> '||'<strong>'||ds_item_w||' : '||'</strong>'||ds_resultado_w|| ' ' || ds_complemento_w || ' </p><p> </p>';
 		else 
			ds_conteudo_w := ds_conteudo_w ||'<p> '||'<strong>'||ds_item_w||' : '||'</strong>'||ds_resultado_w||' </p><p> </p>';
		end if;
		end;
	end loop;
	close C01;
	 
	ds_titulo_rft_w := '<p><strong>'|| wheb_mensagem_pck.get_texto(326719)|| '</strong>'||ds_titulo_w || '</p>'|| ds_enter_w;	
	 
	ds_evolucao_w	:= ds_titulo_rft_w ||ds_conteudo_w;
	 
	select	coalesce(max(ie_tipo_evolucao),'1') 
	into STRICT	ie_tipo_evolucao_w 
	from	usuario 
	where	nm_usuario = nm_usuario_p;
	 
	select	nextval('evolucao_paciente_seq') 
	into STRICT	cd_evolucao_w 
	;
 
	select 	coalesce(ie_relev_resumo_alta,'N') 
  into STRICT 	ie_relev_resumo_alta_w 
	from 	tipo_evolucao 
  where 	cd_tipo_evolucao = ie_evolucao_clinica_w;
	 
	insert into evolucao_paciente(	cd_evolucao, 
					dt_evolucao, 
					ie_tipo_evolucao, 
					cd_pessoa_fisica, 
					dt_atualizacao, 
					nm_usuario, 
					nr_atendimento, 
					ds_evolucao, 
					cd_medico, 
					IE_EVOLUCAO_CLINICA, 
					ie_situacao, 
					nr_seq_avaliacao, 
					cd_setor_atendimento, 
					IE_EVOLUCAO_DOR, 
					IE_RECEM_NATO, 
					IE_AVALIADOR_AUX, 
					NM_USUARIO_NREC,					 
					ie_relev_resumo_alta, 
					nr_cirurgia, 
					nr_seq_pepo, 
					IE_NIVEL_ATENCAO) 
			values (	cd_evolucao_w, 
					dt_avaliacao_w, 
					ie_tipo_evolucao_w, 
					cd_pessoa_fisica_w, 
					clock_timestamp(), 
					nm_usuario_p, 
					nr_atendimento_w, 
					ds_evolucao_w, 
					cd_medico_w, 
					IE_EVOLUCAO_CLINICA_w,	 
					'A', 
					nr_sequencia_p, 
					obter_setor_atendimento(nr_atendimento_w), 
					'N', 
					'N', 
					'N', 
					NM_USUARIO_P, 
					ie_relev_resumo_alta_w, 
					nr_cirurgia_w, 
					nr_seq_pepo_w, 
					wheb_assist_pck.get_nivel_atencao_perfil);
					 
  if (nr_seq_soap_w > 0) then 
  
   CALL gerar_vinculo_soap(nr_seq_soap_w, 'S', nm_usuario_p, cd_evolucao_w);
   cd_evolucao_r_w := gerar_evolucao_soap(nr_atendimento_w, nm_usuario_p, cd_evolucao_r_w);
	 
 end if;
	 
	CALL Liberar_Evolucao(cd_evolucao_w,nm_usuario_p);
	 
	if (obter_se_contido(ie_tipo_evolucao_w,ds_Funcao_Nutricao_w) = 'S') then 
		CALL atualizar_atend_profissional(nr_atendimento_w,cd_medico_w,'N', nm_usuario_p );
	end if;
	if (nr_sequencia_p > -1) then 
		CALL gerar_evolucao_vinculo(nr_sequencia_p,cd_evolucao_w,'AV');
	end if;
	 
	 
end if;
 
 
 
if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_evolucao_avaliacao_soap ( nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

