SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_evolucao_emerges ( NR_ATENDIMENTO_P bigint, DS_FLUXOGRAMA_P text, DS_QUEIXA_P text, DS_OBSERVACAO_P text, DS_USUARIO_P text, NR_CONSELHO_P text, DS_DISCRIMINADOR_P text, DS_ALERGIAS_P text default '', DS_SINAISINFECCAO_P text default '', DS_HISTORICODESCRICAO_P text default '', DS_HISTORICODOENCAS_P text default '', DS_ALERGIASDESCRICAO_P text default '') AS $body$
DECLARE

									
									
									
ds_texto_w	varchar(32000);
quebra_w	varchar(255):= wheb_rtf_pck.get_quebra_linha;
cd_pessoa_fisica_w	varchar(10);
IE_TIPO_EVOLUCAO_w	varchar(10);
nm_usuario_w		varchar(15);
cd_medico_w		varchar(15);
dt_inicio_atendimento_w	varchar(20);
dt_fim_triagem_w	varchar(20);
ds_prioridade_triagem_w	varchar(60);


BEGIN

begin

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p	> 0) then
	
	ds_texto_w	:= wheb_rtf_pck.get_cabecalho;


	select	max(cd_pessoa_fisica),
		max(to_char(dt_inicio_atendimento, 'dd/mm/yyyy hh24:mi:ss')),
		max(to_char(dt_fim_triagem, 'dd/mm/yyyy hh24:mi:ss')),
		max(obter_desc_triagem(nr_seq_triagem))
	into STRICT	cd_pessoa_fisica_w,
		dt_inicio_atendimento_w,
		dt_fim_triagem_w,
		ds_prioridade_triagem_w
	from	atendimento_paciente
	where	nr_atendimento	= nr_atendimento_p;
	
	nm_usuario_w	:= upper(ds_usuario_p);
	
	
	select	max(ie_tipo_evolucao),
			max(cd_pessoa_fisica)
	into STRICT	ie_tipo_evolucao_w,
			cd_medico_w
	from	usuario
	where	upper(nm_usuario)	= nm_usuario_w;
	
	
	
	if (ds_queixa_p IS NOT NULL AND ds_queixa_p::text <> '') then
		ds_texto_w	:= ds_texto_w || quebra_w||'\b ' || obter_desc_expressao(774088)|| ': ' || '\b0'  || ds_queixa_p || quebra_w;
	end if;

	if (ds_fluxograma_p IS NOT NULL AND ds_fluxograma_p::text <> '') then
		ds_texto_w	:= ds_texto_w || quebra_w||'\b ' || obter_desc_expressao(774089)|| ': ' || '\b0 '  || ds_fluxograma_p || quebra_w;
	end if;
	
	if (ds_discriminador_p IS NOT NULL AND ds_discriminador_p::text <> '') then
		ds_texto_w	:= ds_texto_w || quebra_w || '\b ' || obter_desc_expressao(303929) || ': ' || '\b0 '   || ds_discriminador_p || quebra_w;
	end if;
		
	if (ds_prioridade_triagem_w IS NOT NULL AND ds_prioridade_triagem_w::text <> '') then
		ds_texto_w	:= ds_texto_w || quebra_w || '\b ' || obter_desc_expressao(774092) || ': ' || '\b0 ' || ds_prioridade_triagem_w || quebra_w;
	end if;	
	
	if (dt_inicio_atendimento_w IS NOT NULL AND dt_inicio_atendimento_w::text <> '') then
		ds_texto_w	:= ds_texto_w || quebra_w || '\b ' || obter_desc_expressao(651459) || ': ' || '\b0 ' || dt_inicio_atendimento_w || quebra_w;
	end if;
	
	if (dt_fim_triagem_w IS NOT NULL AND dt_fim_triagem_w::text <> '') then
		ds_texto_w	:= ds_texto_w || quebra_w || '\b ' || obter_desc_expressao(774093) || ': ' || '\b0 ' || dt_fim_triagem_w || quebra_w;
	end if;
	
	if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then
		ds_texto_w	:= ds_texto_w || quebra_w || '\b ' || obter_desc_expressao(294678) || ': ' || '\b0 ' || ds_observacao_p || quebra_w;
	end if;
	
	
	ds_texto_w	:= ds_texto_w || quebra_w || '\b ' || obter_desc_expressao(338377) || ': ' || '\b0 ' || obter_nome_pf(cd_medico_w) ||' - ' || obter_dados_pf(cd_medico_w,'COPR') || quebra_w;


	ds_texto_w	:= ds_texto_w || wheb_rtf_pck.get_rodape;
	
	select Converte_Acentuacao_Rtf(ds_texto_w)
	into STRICT ds_texto_w
	;
	
	insert into evolucao_paciente(	cd_evolucao,
				dt_evolucao,
				ie_tipo_evolucao,
				cd_pessoa_fisica,
				dt_atualizacao,
				nm_usuario,
				nr_atendimento,
				ds_evolucao,					
				dt_liberacao,
				ie_evolucao_clinica,
				cd_medico,
				nr_cirurgia,
				ie_situacao)
		values (	nextval('evolucao_paciente_seq'),
				clock_timestamp(),
				coalesce(IE_TIPO_EVOLUCAO_w,1),
				cd_pessoa_fisica_w,
				clock_timestamp(),
				nm_usuario_w,
				nr_atendimento_p,
				ds_texto_w,					
				clock_timestamp(),
				'E',
				cd_medico_w,
				null,
				'A');
	


	commit;
	
end if;

exception
when others then
	null;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_evolucao_emerges ( NR_ATENDIMENTO_P bigint, DS_FLUXOGRAMA_P text, DS_QUEIXA_P text, DS_OBSERVACAO_P text, DS_USUARIO_P text, NR_CONSELHO_P text, DS_DISCRIMINADOR_P text, DS_ALERGIAS_P text default '', DS_SINAISINFECCAO_P text default '', DS_HISTORICODESCRICAO_P text default '', DS_HISTORICODOENCAS_P text default '', DS_ALERGIASDESCRICAO_P text default '') FROM PUBLIC;

