SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_evolucao_exame_laudo ( cd_pessoa_fisica_p text, nr_seq_int_prescr_p bigint, ie_tipo_evolucao_p text, ds_evolucao_p text, nm_usuario_p text, ie_liberar_evolucao_p text, nr_atendimento_p bigint, ie_evolucao_clinica_p text) AS $body$
DECLARE

 
cd_evolucao_w		bigint;
cd_medico_w		varchar(10);
cd_setor_atendimento_w	integer;
ie_recem_nato_w		varchar(1);
ie_tipo_evolucao_w	varchar(10);


BEGIN 
 
	--ie_tipo_evolucao_w := nvl(ie_tipo_evolucao_p, ie_evolucao_clinica_p); 
	 
	select	coalesce(max(ie_tipo_evolucao),'1') 
	into STRICT	ie_tipo_evolucao_w 
	from	usuario 
	where	nm_usuario = nm_usuario_p;
	 
		 
	select nextval('evolucao_paciente_seq') 
	into STRICT	cd_evolucao_w 
	;
	 
	select max(a.cd_pessoa_fisica) 
	into STRICT  cd_medico_w 
	from  usuario a 
	where a.nm_usuario = nm_usuario_p;
	 
	select max(a.cd_setor_atendimento) 
	into STRICT  cd_setor_atendimento_w 
	from  atend_paciente_unidade a 
	where a.nr_seq_interno = ( 
			SELECT Obter_Atepacu_paciente(nr_atendimento_p, 'A') 
			);	
	 
	select	max(CASE WHEN ie_recem_nato='S' THEN  'S'  ELSE 'N' END ) 
	into STRICT	ie_recem_nato_w 
	from	prescr_medica b, 
		prescr_procedimento a		 
	where	a.nr_prescricao = b.nr_prescricao 
	and	a.nr_seq_interno = nr_seq_int_prescr_p;
	 
	insert into evolucao_paciente(	cd_evolucao, 
					dt_evolucao, 
					ie_tipo_evolucao, 
					cd_pessoa_fisica, 
					dt_atualizacao, 
					nm_usuario, 
					ds_evolucao, 
					nr_seq_int_prescr, 
					nr_atendimento, 
					cd_medico, 
					cd_setor_atendimento, 
					ie_situacao, 
					ie_evolucao_dor, 
					ie_recem_nato, 
					ie_evolucao_clinica) 
			values (	cd_evolucao_w, 
					clock_timestamp(), 
					ie_tipo_evolucao_w, 
					cd_pessoa_fisica_p, 
					clock_timestamp(), 
					nm_usuario_p, 
					ds_evolucao_p, 
					nr_seq_int_prescr_p, 
					nr_atendimento_p, 
					cd_medico_w, 
					cd_setor_atendimento_w, 
					'S', 
					'N', 
					ie_recem_nato_w, 
					ie_evolucao_clinica_p);
if (ie_liberar_evolucao_p = 'S') then 
	CALL liberar_evolucao(cd_evolucao_w,nm_usuario_p);
	null;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_evolucao_exame_laudo ( cd_pessoa_fisica_p text, nr_seq_int_prescr_p bigint, ie_tipo_evolucao_p text, ds_evolucao_p text, nm_usuario_p text, ie_liberar_evolucao_p text, nr_atendimento_p bigint, ie_evolucao_clinica_p text) FROM PUBLIC;

