SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_evolucao_lib_reaval (libera_evolucao_p text, nr_atendimento_p bigint, nm_usuario_p text, ie_evolucao_clinica_p text, ds_evolucao_p text, cd_evolucao_p INOUT text) AS $body$
DECLARE

 
cd_evolucao_w 		varchar(10);
cd_medico_w   	varchar(10);
ie_tipo_evolucao_w  	varchar(10);
cd_pessoa_fisica_w  	varchar(10);
cd_especialidade_medico_w varchar(10);
ds_evolucao_w		varchar(32000);


BEGIN 
 
cd_evolucao_p := null;
 
select	coalesce(max(ie_tipo_evolucao),1), 
    max(cd_pessoa_fisica) 
into STRICT 	ie_tipo_evolucao_w, 
	cd_medico_w 
from 	usuario 
where 	nm_usuario = nm_usuario_p;
 
select	cd_pessoa_fisica 
into STRICT  	cd_pessoa_fisica_w 
from 	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
 
cd_especialidade_medico_w := Obter_Especialidade_medico(cd_medico_w,'E');
 
ds_evolucao_w := wheb_rtf_pck.get_cabecalho||ds_evolucao_p||wheb_rtf_pck.get_rodape;
 
insert into 	evolucao_paciente( 
		cd_evolucao, 
		dt_evolucao, 
		ie_tipo_evolucao, 
		cd_pessoa_fisica, 
		nr_atendimento, 
		cd_medico, 
		cd_especialidade_medico, 
		ie_evolucao_clinica, 
		ie_recem_nato, 
		ie_situacao, 
		dt_atualizacao, 
		nm_usuario, 
		ds_evolucao 
		) 
values ( 
		nextval('evolucao_paciente_seq'), 
		clock_timestamp(), 
		ie_tipo_evolucao_w, 
		cd_pessoa_fisica_w, 
		nr_atendimento_p, 
		cd_medico_w, 
		cd_especialidade_medico_w, 
		ie_evolucao_clinica_p, 
		'N', 
		'A', 
		clock_timestamp(), 
		nm_usuario_p, 
		ds_evolucao_w 
		);
commit;
 
if	libera_evolucao_p = 'S' then 
	select 	max(cd_evolucao) 
	into STRICT	cd_evolucao_w 
	from 	evolucao_paciente 
	where 	nr_atendimento = nr_atendimento_p;
	CALL Liberar_Evolucao(cd_evolucao_w,nm_usuario_p);
	cd_evolucao_p	:= cd_evolucao_w;
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
