CREATE OR REPLACE PROCEDURE gerar_evolucao_odonto (cd_pessoa_fisica_p text, nr_atendimento_p bigint, ds_evolucao_p text, nm_usuario_p text, cd_evolucao_p INOUT bigint) AS $body$
DECLARE

 
cd_medico_w	varchar(10);
cd_tipo_evolucao_w tipo_evolucao.cd_tipo_evolucao%type := Obter_Param_Usuario_padrao(281,226, obter_perfil_ativo, nm_usuario_p ,obter_estabelecimento_ativo);
	

BEGIN 
 
SELECT CASE WHEN count(*)=0 THEN null  ELSE cd_tipo_evolucao_w END  
into STRICT cd_tipo_evolucao_w 
FROM tipo_evolucao 
WHERE cd_tipo_evolucao = cd_tipo_evolucao_w 
and ie_odontologia = 'S';
 
if (ds_evolucao_p IS NOT NULL AND ds_evolucao_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	cd_medico_w	:= Obter_Pf_Usuario(nm_usuario_p,'C');
	 
	insert into evolucao_paciente(	 
		cd_evolucao, 
		dt_evolucao, 
		ie_evolucao_clinica, 
		ie_tipo_evolucao, 
		cd_pessoa_fisica, 
		dt_atualizacao, 
		nm_usuario, 
		ds_evolucao, 
		nr_atendimento, 
		cd_medico, 
		ie_situacao) 
	values (	nextval('evolucao_paciente_seq'), 
		clock_timestamp(), 
		cd_tipo_evolucao_w, 
		'ODT', 
		cd_pessoa_fisica_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		ds_evolucao_p, 
		nr_atendimento_p, 
		cd_medico_w, 
		'A') returning cd_evolucao 
		into cd_evolucao_p;
end if;
 
commit;
 
end;
$body$
