SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_evolucao_orient_alta ( nr_sequencia_p bigint, ie_tipo_evolucao_p text, cd_pessoa_fisica_p text, nm_usuario_p text, ie_pronto_atendimento_p text default 'N') AS $body$
DECLARE


ie_evolucao_clinica_w   varchar(255);
cd_evolucao_w		bigint;
dt_atualizacao_w	timestamp;
ds_funcao_nutricicao_w	varchar(255);
ie_consiste_w		varchar(255);

nr_atendimento_w	bigint;
cd_medico_w			varchar(10);


BEGIN

if (ie_tipo_evolucao_p IS NOT NULL AND ie_tipo_evolucao_p::text <> '') then

	ds_funcao_nutricicao_w := Obter_Valor_Param_Usuario(281, 174, Obter_Perfil_Ativo, nm_usuario_p, obter_estabelecimento_ativo);

	If (ie_pronto_atendimento_p = 'S') then
		ie_evolucao_clinica_w := Obter_Valor_Param_Usuario(935, 132, Obter_Perfil_Ativo, nm_usuario_p, obter_estabelecimento_ativo);
	Else
		ie_evolucao_clinica_w := Obter_Valor_Param_Usuario(281, 693, Obter_Perfil_Ativo, nm_usuario_p, obter_estabelecimento_ativo);
	End if;	

	select	nextval('evolucao_paciente_seq')
	into STRICT	cd_evolucao_w
	;
	
	select	dt_atualizacao
	into STRICT	dt_atualizacao_w
	from	atendimento_alta
	where	nr_sequencia	= nr_sequencia_p;	

	Insert into evolucao_paciente(
		cd_evolucao,
		dt_evolucao,
		ie_tipo_evolucao,
		cd_pessoa_fisica,
		dt_atualizacao,
		nm_usuario,
		nr_atendimento,
		ds_evolucao,
		cd_medico,
		dt_liberacao,
		ie_evolucao_clinica,
		cd_setor_atendimento,
		cd_especialidade,
		cd_medico_parecer,
		ie_recem_nato,
		ie_situacao)
	SELECT	cd_evolucao_w,
		dt_atualizacao,
		ie_tipo_evolucao_p,
		obter_pessoa_atendimento(nr_atendimento,'C'),
		clock_timestamp(),
		nm_usuario_p,
		nr_atendimento,
		' ',
		cd_pessoa_fisica_p,
		null,
		coalesce(ie_evolucao_clinica_w,'E'),
		obter_setor_atendimento(nr_atendimento),
		null,
		null,
		'N',
		'A'
	from	atendimento_alta
	where	nr_sequencia	= nr_sequencia_p;

	commit;

	CALL copia_campo_long_de_para(	'ATENDIMENTO_ALTA',
					'DS_ORIENTACAO',
					' where nr_sequencia = :nr_sequencia ',
					'nr_sequencia='||nr_sequencia_p,
					'EVOLUCAO_PACIENTE',
					'DS_EVOLUCAO',
					' where cd_evolucao = :cd_evolucao',
					'cd_evolucao='||cd_evolucao_w);
					
	update 	evolucao_paciente
	set	dt_liberacao = dt_atualizacao_w,
		dt_atualizacao = dt_atualizacao_w
	where	cd_evolucao = cd_evolucao_w;
	
	commit;
	
	CALL gerar_evolucao_vinculo(nr_sequencia_p,cd_evolucao_w,'OA');
	
	
	if (ds_funcao_nutricicao_w IS NOT NULL AND ds_funcao_nutricicao_w::text <> '') and (obter_funcao_ativa = 281) then
		
		select 	obter_se_contido(ie_tipo_evolucao_p,ds_funcao_nutricicao_w)
		into STRICT	ie_consiste_w	
		;
	
	
		if (ie_consiste_w = 'S') then
		
			Select  max(nr_atendimento),
					max(cd_medico)
			into STRICT	nr_atendimento_w,
					cd_medico_w
			from	evolucao_paciente
			where	cd_evolucao = cd_evolucao_w;
			
			if (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '')  and (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then
		
				CALL atualizar_atend_profissional(nr_atendimento_w,cd_medico_w,'N',nm_usuario_p);
				
			end if;
		
		end if;
	
	end if;	

end if;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
