CREATE OR REPLACE PROCEDURE gerar_evolucao_parecer ( nr_parecer_p bigint, nr_sequencia_p bigint, ie_tipo_evolucao_p text, nm_usuario_p text, cd_evolucao_p bigint default null) AS $body$
DECLARE

 
cd_evolucao_w		bigint;
ie_relev_resumo_alta_w	varchar(1);
is_aplicacao_controla_copia_w boolean := false;

BEGIN
 
if (coalesce(cd_evolucao_p::text, '') = '')then 
	select	nextval('evolucao_paciente_seq') 
	into STRICT	cd_evolucao_w 
	;
else 
	begin 
	cd_evolucao_w := cd_evolucao_p;
		is_aplicacao_controla_copia_w := true;
	end;
end if;
	 
 
select	coalesce(max(ie_relev_resumo_alta),'N') 
into STRICT	ie_relev_resumo_alta_w 
from	tipo_evolucao 
where	cd_tipo_evolucao = 'R';
 
Insert into evolucao_paciente( 
	cd_evolucao, 
	dt_evolucao, 
	ie_tipo_evolucao, 
	cd_pessoa_fisica, 
	dt_atualizacao, 
	nm_usuario, 
	nr_atendimento, 
	ds_evolucao, 
	cd_medico, 
	dt_liberacao, 
	ie_evolucao_clinica, 
	cd_setor_atendimento, 
	cd_especialidade, 
	cd_especialidade_medico, 
	cd_medico_parecer, 
	ie_recem_nato, 
	ie_situacao, 
	ie_relev_resumo_alta) 
SELECT cd_evolucao_w, 
    clock_timestamp(), 
    ie_tipo_evolucao_p, 
    b.cd_pessoa_fisica, 
    clock_timestamp(), 
    nm_usuario_p, 
    b.nr_atendimento, 
    ' ', 
    a.cd_medico, 
    null, 
    'R', 
    obter_setor_atendimento(b.nr_atendimento), 
    null, 
	  CASE WHEN obter_se_especialidade_medico(a.cd_medico,b.cd_especialidade_dest)='S' THEN b.cd_especialidade_dest  ELSE null END , 
    null, 
    'N', 
    'A', 
	  ie_relev_resumo_alta_w 
from  	PARECER_MEDICO a, 
		PARECER_MEDICO_REQ b 
where 	a.nr_parecer = b.nr_parecer 
and  	b.nr_parecer = nr_parecer_p 
and		a.nr_sequencia = nr_sequencia_p;
 
commit;
 
if (is_aplicacao_controla_copia_w = false)then 
	CALL copia_campo_long_de_para(	'PARECER_MEDICO', 
					'DS_PARECER', 
					' where nr_parecer = :nr_parecer_seq and nr_sequencia = :nr_seq ', 
					'nr_parecer_seq='||nr_parecer_p||';nr_seq='||nr_sequencia_p, 
					'EVOLUCAO_PACIENTE', 
					'DS_EVOLUCAO', 
					' where cd_evolucao = :cd_evolucao', 
					'cd_evolucao='||cd_evolucao_w);
end if;					
 
CALL liberar_evolucao(cd_evolucao_w,nm_usuario_p);
 
CALL gerar_evolucao_vinculo(nr_parecer_p,cd_evolucao_w,'RP', nr_sequencia_p);
 
commit;
 
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
