CREATE OR REPLACE PROCEDURE gerar_evolucao_precaucao ( ie_tipo_evolucao_p text, nr_atendimento_p bigint, ds_texto_p text, cd_medico_p text, nm_usuario_p text, nr_sequencia_p bigint default null) AS $body$
DECLARE

					 
cd_pessoa_fisica_w	varchar(10);
cd_medico_w			varchar(10);
ie_evol_profiss_w	varchar(3);
cd_evolucao_w		bigint;


BEGIN 
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
select	max(ie_tipo_evolucao), 
	max(cd_pessoa_fisica) 
into STRICT	ie_evol_profiss_w, 
	cd_medico_w 
from	usuario 
where	nm_usuario = nm_usuario_p;
 
select	nextval('evolucao_paciente_seq') 
into STRICT	cd_evolucao_w
;
 
if (ie_tipo_evolucao_p IS NOT NULL AND ie_tipo_evolucao_p::text <> '') then 
	insert into evolucao_paciente(	cd_evolucao, 
					dt_evolucao, 
					ie_tipo_evolucao, 
					ie_evolucao_clinica, 
					cd_pessoa_fisica, 
					dt_atualizacao, 
					nm_usuario, 
					nr_atendimento, 
					ds_evolucao,					 
					dt_liberacao, 
					cd_medico, 
					ie_situacao) 
			values (	cd_evolucao_w, 
					clock_timestamp(), 
					ie_evol_profiss_w, 
					ie_tipo_evolucao_p, 
					cd_pessoa_fisica_w, 
					clock_timestamp(), 
					nm_usuario_p, 
					nr_atendimento_p, 
					ds_texto_p, 
					clock_timestamp(), 
					coalesce(cd_medico_p,cd_medico_w), 
					'A');					
				 
	if (nr_sequencia_p > -1) then 
		CALL gerar_evolucao_vinculo(nr_sequencia_p,cd_evolucao_w,'PR');
	end if;
					 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
