CREATE OR REPLACE PROCEDURE gerar_evolucao_remocao ( nr_seq_remocao_p bigint, ie_tipo_evolucao_p text, nr_atendimento_p bigint, cd_profissional_p text, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


ds_patologia_w		varchar(254);
cd_evolucao_w		bigint;
ie_ja_possui_evolucao_w	varchar(1) := 'N';

BEGIN

select	ds_patologia
into STRICT	ds_patologia_w
from	eme_regulacao
where	nr_sequencia = nr_seq_remocao_p;

select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_ja_possui_evolucao_w
from	evolucao_paciente
where	nr_seq_remocao = nr_seq_remocao_p;

select	nextval('evolucao_paciente_seq')
into STRICT	cd_evolucao_w
;

if ( ie_ja_possui_evolucao_w = 'N') then
	insert into evolucao_paciente(	cd_evolucao,
					dt_evolucao,
					ie_tipo_evolucao,
					cd_pessoa_fisica,
					dt_atualizacao,
					nm_usuario,
					ds_evolucao,
					dt_liberacao,
					ie_situacao,
					cd_medico,
					nr_atendimento,
					nr_seq_remocao)
	values (cd_evolucao_w,
					clock_timestamp(),
					ie_tipo_evolucao_p,
					cd_pessoa_fisica_p,
					clock_timestamp(),
					nm_usuario_p,
					ds_patologia_w,
					clock_timestamp(),
					'A',
					cd_profissional_p,
					nr_atendimento_p,
					nr_seq_remocao_p);
else
update 	evolucao_paciente
set	ds_evolucao    = ds_patologia_w,
	dt_atualizacao = clock_timestamp()
where	nr_seq_remocao = nr_seq_remocao_p;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
