SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_exame_apac_pep ( nr_sequencia_p bigint, ie_gerou_p INOUT text) AS $body$
DECLARE



ie_gerou_w			varchar(1) := 'N';
ie_alto_custo_w		varchar(1) := 'N';
ie_atend_sus_w		varchar(1) := 'N';
nr_atendimento_w	bigint;


BEGIN

if ( coalesce(nr_sequencia_p,0) > 0) then

	select 	max(nr_atendimento)
	into STRICT   	nr_atendimento_w
	from   	pedido_exame_externo
	where 	nr_sequencia = nr_sequencia_p;

	if ( coalesce(nr_atendimento_w,0) > 0 ) then

		select	Obter_Se_atend_sus(nr_atendimento_w)
		into STRICT	ie_atend_sus_w
		;

		if (ie_atend_sus_w = 'S') then

			select  Obter_Se_Proc_Apac_Solic(nr_sequencia_p)
			into STRICT	ie_alto_custo_w
			;

			if (ie_alto_custo_w = 'S') then

				CALL gerar_apac_solicit_exame(nr_sequencia_p);
				ie_gerou_w:= 'S';

			end if;

		end if;

	end if;

end if;

ie_gerou_p := ie_gerou_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
