CREATE OR REPLACE PROCEDURE gerar_faq_os ( nr_seq_ordem_p bigint, nr_seq_tipo_faq_p bigint, nr_seq_tipo_lib_faq_p bigint, nr_seq_funcao_p bigint, dt_ordem_p timestamp, ds_pergunta_p text, nm_usuario_p text, ds_palavra_chave_p text) AS $body$
DECLARE


nr_seq_classif_w 				bigint;
nr_seq_grupo_sup_w				bigint;


BEGIN

if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then

select 	max(nr_seq_classif),
	max(coalesce(nr_seq_grupo_sup, null))
into STRICT	nr_seq_classif_w,
	nr_seq_grupo_sup_w
from	man_ordem_servico
where	nr_sequencia = nr_seq_ordem_p;

insert into 	com_faq(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_pergunta,
			ds_pergunta,
			nr_seq_tipo,
			nr_seq_tipo_lib,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_funcao,
			nr_seq_ordem,
			nr_seq_subclassif_os,
			nr_seq_grupo_sup,
			ds_palavra_chave,
			ie_situacao)
		values (	nextval('com_faq_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			ds_pergunta_p,
			nr_seq_tipo_faq_p,
			coalesce(nr_seq_tipo_lib_faq_p, null),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_funcao_p,
			nr_seq_ordem_p,
			nr_seq_classif_w,
			nr_seq_grupo_sup_w,
			ds_palavra_chave_p,
			'A');
end if;

commit;

end;
$body$
