CREATE OR REPLACE PROCEDURE gerar_faturamento_unidade_w ( cd_setor_atendimento_p bigint, cd_convenio_p bigint, dt_inicial_p timestamp, dt_final_p timestamp ) AS $body$
DECLARE

 
nr_atendimento_w		bigint;
cd_setor_atendimento_w		integer;
cd_convenio_w			integer;
cd_proc_mat_w			bigint;
ie_origem_proced_w		bigint;
ds_proc_mat_w			procedimento.ds_procedimento%TYPE;
qt_proc_mat_w			double precision;
vl_proc_mat_w			double precision;
vl_custo_w			double precision;
vl_diferenca_w			double precision;
ds_comando_w			varchar(2000);
ie_proc_mat_w			varchar(1);
ie_insere_w			varchar(1);

C01 CURSOR FOR 
SELECT	'P' ie_proc_mat, 
	b.nr_atendimento, 
	obter_convenio_atendimento(b.nr_atendimento) cd_convenio, 
	c.cd_procedimento cd_proc_mat, 
	c.ie_origem_proced, 
	obter_descricao_procedimento(c.cd_procedimento,c.ie_origem_proced) ds_proc_mat, 
	sum(coalesce(c.qt_procedimento,0)) qt_proc_mat, 
	sum(coalesce(c.vl_procedimento,0)) vl_proc_mat, 
	0 vl_custo, 
	sum(coalesce(c.vl_procedimento,0)) vl_diferenca 
from	procedimento_paciente c, 
	atend_paciente_unidade a, 
	atendimento_paciente b 
where	a.nr_atendimento = b.nr_atendimento - 0 
and	b.nr_atendimento = c.nr_atendimento 
and	a.cd_setor_atendimento = 12 
and	(b.dt_alta IS NOT NULL AND b.dt_alta::text <> '') 
and	b.dt_alta >= dt_inicial_p 
and	b.dt_alta <= dt_final_p 
and	((obter_convenio_atendimento(b.nr_atendimento) = cd_convenio_p) or (coalesce(cd_convenio_p::text, '') = '')) 
group by	b.nr_atendimento, 
		c.cd_procedimento, 
		c.ie_origem_proced 

union
 
SELECT	'M' ie_proc_mat, 
	b.nr_atendimento, 
	obter_convenio_atendimento(b.nr_atendimento) cd_convenio, 
	c.cd_material cd_proc_mat, 
	0 ie_origem_proced, 
	obter_desc_material(c.cd_material) ds_proc_mat, 
	sum(coalesce(c.qt_material,0)) qt_proc_mat, 
	sum(coalesce(c.vl_material,0)) vl_proc_mat, 
	coalesce(obter_valor_ultima_compra(b.cd_estabelecimento,365,c.cd_material,null,'N'),0) vl_custo, 
	coalesce((sum(c.vl_material) - sum(obter_valor_ultima_compra(b.cd_estabelecimento,365,c.cd_material,null,'N'))),0) vl_diferenca 
from	material_atend_paciente c, 
	atend_paciente_unidade a, 
	atendimento_paciente b 
where	a.nr_atendimento = b.nr_atendimento - 0 
and	b.nr_atendimento = c.nr_atendimento 
and	a.cd_setor_atendimento = 12 
and	(b.dt_alta IS NOT NULL AND b.dt_alta::text <> '') 
and	b.dt_alta >= dt_inicial_p 
and	b.dt_alta <= dt_final_p 
and	((obter_convenio_atendimento(b.nr_atendimento) = cd_convenio_p) or (coalesce(cd_convenio_p::text, '') = '')) 
and	(c.cd_material IS NOT NULL AND c.cd_material::text <> '') 
and	obter_tipo_material(c.cd_material,'C') = 1 
group by	b.nr_atendimento, 
		c.cd_material, 
		b.cd_estabelecimento 
order by	2;


BEGIN 
 
begin 
 
CALL EXEC_SQL_DINAMICO('TASY','drop table faturamento_unidade_w');
CALL EXEC_SQL_DINAMICO('TASY','create table faturamento_unidade_W (nr_atendimento number(10), cd_setor_atendimento number(5), 
			 cd_convenio number(5), ie_proc_mat varchar2(1), cd_proc_mat number(15), ie_origem_proced number(1), ds_proc_mat varchar2(240), 
			 qt_proc_mat number (9,3), vl_proc_mat number (15,2), vl_custo number (15,2), vl_diferenca number(15,2))');
end;
 
OPEN C01;
LOOP 
	fetch C01 into	ie_proc_mat_w, 
			nr_atendimento_w, 
			cd_convenio_w, 
			cd_proc_mat_w, 
			ie_origem_proced_w, 
			ds_proc_mat_w, 
			qt_proc_mat_w, 
			vl_proc_mat_w, 
			vl_custo_w, 
			vl_diferenca_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
 
/* Setor atendimento atual */
 
Select	obter_setor_atepacu(obter_atepacu_paciente(nr_atendimento_w,'IAA'),0) 
into STRICT	cd_setor_atendimento_w
;
 
	if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') and (cd_setor_atendimento_w <> cd_setor_atendimento_p) then 
		ie_insere_w	:= 'N';
	else 
		ie_insere_w	:= 'S';
	end if;
 
	if (ie_insere_w = 'S') then 
		ds_comando_w		:= 	'insert into faturamento_unidade_w values (' || nr_atendimento_w ||', '|| cd_setor_atendimento_w || ', ' || 
						cd_convenio_w || ', ' || chr(39) || ie_proc_mat_w || chr(39) || ', ' ||cd_proc_mat_w || ', ' || ie_origem_proced_w 
						|| ', ' || chr(39) || ds_proc_mat_w || chr(39) || ', ' || qt_proc_mat_w || ', ' || replace(to_char(vl_proc_mat_w),',','.') 
						|| ', ' || replace(to_char(vl_custo_w),',','.') || ', ' || replace(to_char(vl_diferenca_w),',','.') || ')';
 
		CALL EXEC_SQL_DINAMICO('TASY',ds_comando_w);
	end if;
 
end loop;
close C01;
 
commit;
 
END;
$body$
