CREATE OR REPLACE PROCEDURE gerar_feriado_todos_estab ( cd_estabelecimento_p bigint, dt_feriado_p timestamp, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;
cd_estabelecimento_w		smallint;
dt_feriado_w			timestamp;
ds_motivo_feriado_w		varchar(40);
ie_tipo_feriado_w		varchar(15);

c01 CURSOR FOR
SELECT	cd_estabelecimento
from	estabelecimento
where	cd_estabelecimento <> cd_estabelecimento_p;


BEGIN

select	max(ds_motivo_feriado),
	max(ie_tipo_feriado)
into STRICT	ds_motivo_feriado_w,
	ie_tipo_feriado_w
from	feriado
where	cd_estabelecimento = cd_estabelecimento_p
and	dt_feriado = dt_feriado_p;

open C01;
loop
fetch C01 into
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	feriado
	where	dt_feriado = dt_feriado_p
	and	cd_estabelecimento = cd_estabelecimento_w;

	if (qt_existe_w = 0) then
		begin
		insert into feriado(
			cd_estabelecimento,
			dt_feriado,
			ds_motivo_feriado,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo_feriado)
		values (	cd_estabelecimento_w,
			dt_feriado_p,
			ds_motivo_feriado_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ie_tipo_feriado_w);
		end;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
