SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_fila_atend_futuro ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w    	bigint;
cd_cid_w		varchar(10);
cd_especialidade_w	integer;
cd_convenio_w		integer;
cd_medico_w		varchar(10);
ie_origem_proced_w	bigint;
cd_procedimento_w	bigint;
nr_sequencia_proc_w	bigint;
nr_seq_proc_interno_w	bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select 	max(cd_pessoa_fisica),
		max(cd_especialidade),
		max(CASE WHEN obter_convenio_atendimento(nr_atend_origem)=0 THEN null  ELSE obter_convenio_atendimento(nr_atend_origem) END ),
		max(obter_cid_atendimento(nr_atend_origem,'P')),
		max(cd_medico)
	into STRICT 	cd_pessoa_fisica_w,
		cd_especialidade_w,
		cd_convenio_w,
		cd_cid_w,
		cd_medico_w
	from 	atend_pac_futuro
	where   nr_sequencia = nr_sequencia_p;


	select	max(nr_sequencia)
	into STRICT	nr_sequencia_proc_w
	from	atend_pac_proced_previsto
	where	nr_seq_atend_futuro = nr_sequencia_p
	and	ie_proc_principal = 'S';

	if (nr_sequencia_proc_w IS NOT NULL AND nr_sequencia_proc_w::text <> '') then

		select	max(cd_procedimento),
			max(ie_origem_proced),
			max(nr_seq_proc_interno)
		into STRICT	cd_procedimento_w,
			ie_origem_proced_w,
			nr_seq_proc_interno_w
		from	atend_pac_proced_previsto
		where	nr_sequencia = nr_sequencia_proc_w;

	end if;

end if;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

	insert into paciente_espera(
					nr_sequencia,
					cd_pessoa_fisica,
					nr_seq_atend_futuro,
					nr_prioridade,
					dt_atualizacao,
					nm_usuario,
					ie_situacao,
					dt_inclusao,
					cd_especialidade_medica,
					cd_convenio,
					cd_cid_doenca,
					cd_medico,
					cd_procedimento,
					ie_origem_proced,
					nr_seq_proc_interno
					)
				values (
					nextval('paciente_espera_seq'),
					cd_pessoa_fisica_w,
					nr_sequencia_p,
					1,
					clock_timestamp(),
					nm_usuario_p,
					'A',
					clock_timestamp(),
					cd_especialidade_w,
					cd_convenio_w,
					cd_cid_w,
					cd_medico_w,
					cd_procedimento_w,
					ie_origem_proced_w,
					nr_seq_proc_interno_w
					);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
