CREATE OR REPLACE PROCEDURE gerar_fluxo_caixa_composicao (dt_referencia_p timestamp, ie_composicao_p text, vl_composicao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*--------------------------------------------------------------- ATENÇÃO ----------------------------------------------------------------*/

/* Cuidado ao realizar alterações no fluxo de caixa. Toda e qualquer alteração realizada em qualquer uma das       */

/* procedures do fluxo de caixa deve ser cuidadosamente verificada e realizada no fluxo de caixa em lote.           */

/* Devemos garantir que os dois fluxos de caixa tragam os mesmos valores no resultado, evitando assim que           */

/* existam diferenças entre os fluxos de caixa.                                                                                                                */

/*--------------- AO ALTERAR O FLUXO DE CAIXA ALTERAR TAMBÉM O FLUXO DE CAIXA EM LOTE ---------------*/

BEGIN

insert into fluxo_caixa_composicao(	nr_Sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_referencia,
			ie_composicao,
			vl_composicao)
VALUES (	nextval('fluxo_caixa_composicao_seq'),
			clock_timestamp(),
			nm_usuario_p,
			dt_referencia_p,
			ie_composicao_p,
			vl_composicao_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
