CREATE OR REPLACE PROCEDURE gerar_fluxo_caixa_lote_externo ( dt_inicial_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint, cd_empresa_p bigint, ie_restringe_estab_p text, nm_usuario_p text, nr_seq_lote_fluxo_p bigint) AS $body$
DECLARE

 
/*--------------------------------------------------------------- ATENÇÃO ----------------------------------------------------------------*/
 
/* Cuidado ao realizar alterações no fluxo de caixa em lote. Toda e qualquer alteração realizada em qualquer uma */
 
/* das procedures do fluxo de caixa em lote deve ser cuidadosamente verificada e realizada no fluxo de caixa   */
 
/* convencional. Devemos garantir que os dois fluxos de caixa tragam os mesmos valores no resultado, evitando   */
 
/* assim que existam diferenças entre os fluxos de caixa.                                                */
 
/*--------------- AO ALTERAR O FLUXO DE CAIXA EM LOTE ALTERAR TAMBÉM O FLUXO DE CAIXA ---------------*/
 
 
cd_conta_financ_w	bigint;
dt_referencia_w		timestamp;
vl_fluxo_w		double precision;
ie_tratar_fim_semana_w	varchar(1);
ie_fluxo_externo_w	varchar(1);
cd_conta_financ_cre_w	bigint;
dt_inicial_w		timestamp;
dt_final_w		timestamp;

C01 CURSOR FOR 
SELECT	a.cd_conta_financ, 
	trunc(a.dt_documento,'dd'), 
	a.vl_documento 
from	fluxo_caixa_ext a 
where (ie_restringe_estab_p = 'N' or (ie_restringe_estab_p = 'E' and a.cd_estabelecimento = cd_estabelecimento_p) or (ie_restringe_estab_p = 'S' and (a.cd_estabelecimento = cd_estabelecimento_p or obter_estab_financeiro(a.cd_estabelecimento) = cd_estabelecimento_p))) 
and	a.cd_empresa		= cd_empresa_p 
and	a.dt_documento		between	dt_inicial_w and dt_final_w 
and	ie_fluxo_externo_w	= 'S';


BEGIN 
 
dt_inicial_w	:= trunc(dt_inicial_p,'dd');
dt_final_w	:= fim_dia(dt_final_p);
 
select	a.ie_fluxo_externo, 
	a.ie_tratar_fim_semana, 
	a.cd_conta_financ_cre 
into STRICT	ie_fluxo_externo_w, 
	ie_tratar_fim_semana_w, 
	cd_conta_financ_cre_w 
from	parametro_fluxo_caixa a 
where	a.cd_estabelecimento = cd_estabelecimento_p;
 
open C01;
loop 
fetch C01 into	 
	cd_conta_financ_w, 
	dt_referencia_w, 
	vl_fluxo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	if (ie_tratar_fim_semana_w = 'S') then 
		dt_referencia_w	:= obter_proximo_dia_util(cd_estabelecimento_p,dt_referencia_w);
	end if;
	 
	CALL GERAR_FLUXO_CAIXA_LOTE(	dt_referencia_w, 
				cd_conta_financ_w, 
				'', 
				'14', 
				'EX', 
				nm_usuario_p, 
				null, 
				null, 
				null, 
				null, 
				null, 
				null, 
				null, 
				null, 
				nr_seq_lote_fluxo_p, 
				null, 
				null, 
				null, 
				null, 
				null, 
				null, 
				null, 
				coalesce(vl_fluxo_w,0));
	end;
end loop;
close C01;
 
/*NAO COLOCAR COMMIT NESTA PROCEDURE*/
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
