SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_fugulin_sae ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w		bigint;
qt_reg_w			bigint;
cd_profissional_w		varchar(10);
nr_seq_gca_atend_w	bigint;
nr_seq_result_w		bigint;
nr_seq_item_w		bigint;
qt_pontuacao_w		bigint;
qt_pontuacao_total_w	bigint := 0;
nr_seq_item_ref_w		bigint;
ie_ref_item_w		varchar(1);
ie_permite_lote_w		varchar(1);
nr_seq_proj_lib_w		bigint := 51352;

C01 CURSOR FOR
	SELECT	max(f.nr_seq_result),
		c.nr_seq_item
	from	pe_prescricao a,
		pe_prescr_item_result b,
		pe_item_examinar c,
		pe_item_tipo_item d,
		pe_tipo_item e,
		pe_item_resultado f
	where	a.nr_sequencia	= b.nr_seq_prescr
	and	a.nr_sequencia	= nr_sequencia_p
	and	b.nr_seq_item	= c.nr_sequencia
	and	d.nr_seq_item	= c.nr_sequencia
	and	e.nr_sequencia	= d.nr_seq_tipo_item
	and	(c.nr_seq_item IS NOT NULL AND c.nr_seq_item::text <> '')
	and	(f.nr_seq_result IS NOT NULL AND f.nr_seq_result::text <> '')
	and	f.nr_sequencia	= b.nr_seq_result
	group by c.nr_seq_item;
	

BEGIN
select	max(a.cd_prescritor),
	max(a.nr_atendimento)
into STRICT	cd_profissional_w,
	nr_atendimento_w
from	pe_prescricao a,
	pe_prescr_item_result b
where	a.nr_sequencia = b.nr_seq_prescr
and	a.nr_sequencia = nr_sequencia_p;


select	count(*)
into STRICT	qt_reg_w
from	pe_prescricao a,
	pe_prescr_item_result b,
	pe_item_examinar c,
	pe_item_tipo_item d,
	pe_tipo_item e,
	pe_item_resultado f
where	a.nr_sequencia	= b.nr_seq_prescr
and	a.nr_sequencia	= nr_sequencia_p
and	b.nr_seq_item	= c.nr_sequencia
and	d.nr_seq_item	= c.nr_sequencia
and	e.nr_sequencia	= d.nr_seq_tipo_item
and	(c.nr_seq_item IS NOT NULL AND c.nr_seq_item::text <> '')
and	f.nr_sequencia	= b.nr_seq_result;

select 	coalesce(max(ie_permite_lote),'N')
into STRICT   	ie_permite_lote_w
from 	tasy_proj_assin_perfil
where 	nr_seq_proj = nr_seq_proj_lib_w
and 	coalesce(cd_perfil, wheb_usuario_pck.get_cd_perfil) = wheb_usuario_pck.get_cd_perfil;

if (qt_reg_w	> 0) then

	select	max(c.nr_seq_item)
	into STRICT	nr_seq_item_ref_w
	from	pe_prescricao a,
		pe_prescr_item_result b,
		pe_item_examinar c,
		pe_item_tipo_item d,
		pe_tipo_item e,
		pe_item_resultado f
	where	a.nr_sequencia	= b.nr_seq_prescr
	and	a.nr_sequencia	= nr_sequencia_p
	and	b.nr_seq_item	= c.nr_sequencia
	and	d.nr_seq_item	= c.nr_sequencia
	and	e.nr_sequencia	= d.nr_seq_tipo_item
	and	(c.nr_seq_item IS NOT NULL AND c.nr_seq_item::text <> '')
	and	(f.nr_seq_result IS NOT NULL AND f.nr_seq_result::text <> '')
	and	f.nr_sequencia	= b.nr_seq_result;
	
	if (coalesce(nr_seq_item_ref_w,0) > 0) then
	
		select	coalesce(max(ie_autor),'F')
		into STRICT	ie_ref_item_w
		from	gca_item
		where	nr_sequencia = nr_seq_item_ref_w;
	
	end if;


	select	nextval('gca_atendimento_seq')
	into STRICT	nr_seq_gca_atend_w
	;
	
	insert into gca_atendimento(	
					nr_sequencia,
					cd_pessoa_fisica,
					dt_atualizacao,
					dt_avaliacao,
					ie_autor,
					nr_seq_gradacao,
					nm_usuario,
					nr_atendimento,
					ie_situacao,
					dt_liberacao,
					qt_pontuacao)
		values (		nr_seq_gca_atend_w,
					cd_profissional_w,
					clock_timestamp(),
					clock_timestamp(),
					coalesce(ie_ref_item_w,'F'),
					null,
					nm_usuario_p,
					nr_atendimento_w,
					'A',
					clock_timestamp(),
					0);
		if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
	
	open C01;
	loop
	fetch C01 into	
		nr_seq_result_w,
		nr_seq_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		
	select	coalesce(max(qt_ponto),0)
	into STRICT	qt_pontuacao_w
	from	gca_item_result
	where	nr_sequencia	= nr_seq_result_w;
	
	
	qt_pontuacao_total_w	:= qt_pontuacao_total_w + qt_pontuacao_w;
	

		
		insert into gca_atend_result(	
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						nr_seq_avaliacao,
						nr_seq_item,
						nr_seq_result,
						qt_pontuacao)
		values (				nextval('gca_atend_result_seq'),
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_gca_atend_w,
						nr_seq_item_w,
						nr_seq_result_w,
						qt_pontuacao_w);
		end;
	end loop;
	close C01;
	
	
	update	gca_atendimento
	set	qt_pontuacao	= qt_pontuacao_total_w
	where	nr_sequencia	= nr_seq_gca_atend_w;
	
	if (ie_permite_lote_w = 'S') then
	CALL perform_record_signature(nr_atendimento_w,obter_cd_pes_fis_atend(nr_atendimento_w),nm_usuario_p,51352,'GCA_ATENDIMENTO',nr_seq_gca_atend_w,29603,281,'L');
	end if;
	
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_fugulin_sae ( nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

