CREATE OR REPLACE PROCEDURE gerar_funcao_idioma ( cd_funcao_p bigint, nr_seq_idioma_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_atributo_w		varchar(50);
ds_texto_w		varchar(255);
cd_funcao_w		integer;
nr_seq_traducao_w	bigint;

c01 CURSOR FOR
SELECT	'DS_FUNCAO',
	a.ds_funcao,
	a.cd_funcao
from	funcao a
where	a.cd_funcao = cd_funcao_p
and	(a.ds_funcao IS NOT NULL AND a.ds_funcao::text <> '')
and	not exists (
		SELECT	1
		from	funcao_idioma x
		where	x.cd_funcao = a.cd_funcao
		and	x.nm_atributo = 'DS_FUNCAO'
		and	x.nr_seq_idioma = nr_seq_idioma_p);


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nr_seq_idioma_p IS NOT NULL AND nr_seq_idioma_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into	nm_atributo_w,
			ds_texto_w,
			cd_funcao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if ((trim(both ds_texto_w) IS NOT NULL AND (trim(both ds_texto_w))::text <> '')) then
			begin
			select	nextval('funcao_idioma_seq')
			into STRICT	nr_seq_traducao_w
			;

			insert into funcao_idioma(
				nr_sequencia,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_atualizacao,
				nm_usuario,
				nm_atributo,
				ds_descricao,
				cd_funcao,
				nr_seq_idioma,
				ds_traducao,
				ie_necessita_revisao)
			values (
				nr_seq_traducao_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nm_atributo_w,
				ds_texto_w,
				cd_funcao_w,
				nr_seq_idioma_p,
				' ',
				'T');
			end;
		end if;
		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
