SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_ganho_perda_auto ( nm_usuario_p text) AS $body$
DECLARE

 
 
nr_atendimento_w	bigint;
qt_sc_w			double precision;	
cd_setor_atendimento_w	bigint;	
nr_seq_tipo_w		bigint;
qt_volume_w		double precision;	
nr_sequencia_w		bigint;
			
C01 CURSOR FOR 
	SELECT	a.nr_atendimento, 
		a.cd_setor_atendimento 
	from	unidade_atendimento a 
	where	(a.nr_atendimento IS NOT NULL AND a.nr_atendimento::text <> '') 
	and	exists (	SELECT	1 
				from	GANHO_PERDA_AUTO x 
				where	x.cd_setor_atendimento	= a.cd_setor_atendimento);
		
C02 CURSOR FOR 
	SELECT	nr_seq_tipo, 
		qt_volume 
	from	GANHO_PERDA_AUTO 
	where	cd_setor_atendimento	= cd_setor_atendimento_w 
	and	qt_sc_w between QT_SUPERF_CORPORIA_MIN and QT_SUPERF_CORPORIA_MAX;

BEGIN
 
 
open C01;
loop 
fetch C01 into	 
	nr_atendimento_w, 
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	qt_sc_w		:= obter_sinal_vital(nr_atendimento_w,'SC');
	 
	if (coalesce(qt_sc_w,0)	> 0) then 
		nr_seq_tipo_w	:= null;
		open C02;
		loop 
		fetch C02 into	 
			nr_seq_tipo_w, 
			qt_volume_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
		end loop;
		close C02;
		 
		if (nr_seq_tipo_w IS NOT NULL AND nr_seq_tipo_w::text <> '')then 
			nr_sequencia_w := Inserir_ganho_perda(	nr_atendimento_w, null, nr_seq_tipo_w, null, clock_timestamp(), qt_volume_w, 'S', null, null, nm_usuario_p, null, 1, null, nr_sequencia_w);
		end if;
		 
	end if;
	 
	end;
end loop;
close C01;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
