CREATE OR REPLACE PROCEDURE gerar_gasoterapias_rotina ( ds_lista_itens_p text, nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_lista_w		varchar(2000);
aux_w			varchar(2000);
nr_seq_item_w		bigint;
nr_pos_virgula_w	bigint;
nr_pos_hifem_w		bigint := 0;
qt_gasoterapia_w	double precision;
ie_unidade_medida_w	varchar(15);
cd_intervalo_w		varchar(15);
cd_intervalo_gas_w	varchar(15);
		

BEGIN 
if (ds_lista_itens_p IS NOT NULL AND ds_lista_itens_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	ds_lista_w	:= ds_lista_itens_p;
	 
	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop 
		begin 
		nr_pos_virgula_w	:= position(',' in ds_lista_w);
		if (nr_pos_virgula_w > 0) then 
			begin			 
			 
			 
			aux_w			:= substr(ds_lista_w,1,nr_pos_virgula_w-1);							
			 
			nr_pos_hifem_w		:= position('-' in aux_w);
			 
			nr_seq_item_w		:= substr(aux_w,1,nr_pos_hifem_w-1);
			aux_w			:= substr(aux_w,nr_pos_hifem_w+1,length(aux_w));
			 
			nr_pos_hifem_w		:= position('-' in aux_w);
			 
			qt_gasoterapia_w	:= substr(aux_w,1,nr_pos_hifem_w-1);
			aux_w			:= substr(aux_w,nr_pos_hifem_w+1,length(aux_w));
			 
			nr_pos_hifem_w		:= position('-' in aux_w);
			 
			cd_intervalo_w		:= substr(aux_w,1,nr_pos_hifem_w-1);
			aux_w			:= substr(aux_w,nr_pos_hifem_w+1,length(aux_w));
			 
			nr_pos_hifem_w		:= position('-' in aux_w);
			 
			cd_intervalo_gas_w	:= substr(aux_w,1,nr_pos_hifem_w-1);
			aux_w			:= substr(aux_w,nr_pos_hifem_w+1,length(aux_w));
			 
			nr_pos_hifem_w		:= position('-' in aux_w);
			 
			ie_unidade_medida_w	:= substr(aux_w,1,nr_pos_hifem_w-1);			
			 
			ds_lista_w		:= substr(ds_lista_w,nr_pos_virgula_w+1,length(ds_lista_w));			
			 
			end;
		else 
			begin 
			nr_seq_item_w	:= (ds_lista_w)::numeric;
			ds_lista_w	:= null;
			end;
		end if;	
		 
		if (coalesce(nr_seq_item_w,0) > 0) then 
			begin 
			 
			if (ie_unidade_medida_w = '0') then 
				begin 
				ie_unidade_medida_w := null;
				end;
			end if;
			 
			if (cd_intervalo_w = '0') then 
				begin 
				cd_intervalo_w := null;
				end;
			end if;
			 
			if (cd_intervalo_gas_w = '0') then 
				begin 
				cd_intervalo_gas_w := null;
				end;
			end if;
			 
			CALL gerar_gasoterapia_rotina( 
				nr_seq_item_w, 
				nr_prescricao_p, 
				nm_usuario_p, 
				qt_gasoterapia_w, 
				ie_unidade_medida_w, 
				cd_intervalo_w, 
				cd_intervalo_gas_w);
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
