CREATE OR REPLACE PROCEDURE gerar_gestao_migracao ( nm_usuario_p text) AS $body$
DECLARE


cd_perfil_w		integer;
cd_funcao_w		integer;
cd_estabelecimento_w	smallint;

c01 CURSOR FOR
SELECT	cd_perfil,
	cd_estabelecimento
from	perfil
where	ie_situacao = 'A';

c02 CURSOR FOR
SELECT	cd_funcao
from	funcao_perfil
where	cd_perfil = cd_perfil_w;


BEGIN

open C01;
loop
fetch C01 into
	cd_perfil_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		cd_funcao_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		insert into gestao_migracao(nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					cd_perfil,
					cd_funcao,
					ie_delphi,
					ie_java,
					cd_estabelecimento)
				values (nextval('gestao_migracao_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_perfil_w,
					cd_funcao_w,
					'S',
					'N',
					cd_estabelecimento_w);

		commit;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
