CREATE OR REPLACE PROCEDURE gerar_grupo_padrao_local (cd_grupo_material_p bigint, cd_local_estoque_p bigint, cd_local_atende_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w	integer;

C01 CURSOR FOR
SELECT	a.cd_material
from	estrutura_material_v a,
	material_estab	b
where	a.cd_material = b.cd_material
and	b.cd_estabelecimento = cd_estabelecimento_p
and	b.ie_material_estoque = 'S'
and	a.ie_situacao = 'A'
and	a.cd_grupo_material = cd_grupo_material_p
and	not exists (	select 	1
			from 	padrao_estoque_local x
			where 	a.cd_material = x.cd_material
			and 	x.cd_local_estoque = cd_local_estoque_p);

BEGIN
open C01;
loop
fetch C01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into	padrao_estoque_local(cd_material,
				cd_local_estoque,
				cd_local_atende,
				cd_estabelecimento,
				ie_situacao,
				ie_atualiza_padrao_local,
				dt_atualizacao,
				nm_usuario)
			values (cd_material_w,
				cd_local_estoque_p,
				cd_local_atende_p,
				cd_estabelecimento_p,
				'A',
				'N',
				clock_timestamp(),
				nm_usuario_p);
	end;
end loop;
close C01;

commit;
end;
$body$
