		where   a.NR_ATENDIMENTO = NR_ATENDIMENTO_W;

		if (qt_pontos_w = 100) then
		  record_w.IE_GRAU_TABACO_FAGERSTROM 	:= null;
		elsif (qt_pontos_w > 7) then 			--FR 107
