CREATE OR REPLACE PROCEDURE gerar_hier_abas_analise_migr ( cd_funcao_p bigint) AS $body$
DECLARE


nr_seq_objeto_w		bigint;
nm_objeto_orig_w	varchar(50);

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.nm_objeto_orig
from	dic_objeto a
where	a.cd_funcao = cd_funcao_p
and	a.ie_tipo_objeto = 'P'
group by
	a.nr_sequencia,
	a.nm_objeto_orig;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into	nr_seq_objeto_w,
			nm_objeto_orig_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		update	dic_objeto d
		set	d.nr_seq_obj_sup = nr_seq_objeto_w
		where	d.nr_sequencia in (
				SELECT	x.nr_sequencia
				from	dic_objeto x,
					w_analise_migr_proj y
				where	y.cd_funcao = x.cd_funcao
				and	y.nm_objeto = x.nm_objeto_orig
				and	y.ie_objeto = 'TTabSheet'
				and	x.ie_tipo_objeto = 'P'
				and	x.cd_funcao = cd_funcao_p
				and	y.nm_objeto_avo = nm_objeto_orig_w);
		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
