CREATE OR REPLACE PROCEDURE gerar_historico_alterar_status ( NR_SEQUENCIA_P LATAM_HISTORICO.NR_SEQUENCIA%TYPE, IE_LIBERAR_DESENV_P LATAM_HISTORICO.IE_LIBERAR_DESENV%TYPE, DS_OBSERVACAO_P LATAM_HISTORICO.DS_OBSERVACAO%TYPE ) AS $body$
DECLARE

    NM_USUARIO_S          CONSTANT LATAM_HISTORICO.NM_USUARIO%TYPE     := WHEB_USUARIO_PCK.GET_NM_USUARIO;
    DT_ATUAL_S            CONSTANT LATAM_HISTORICO.DT_ATUALIZACAO%TYPE := clock_timestamp();

BEGIN
    INSERT INTO LATAM_HISTORICO(
        NR_SEQUENCIA,
        DT_ATUALIZACAO,
        NM_USUARIO,
        DT_ATUALIZACAO_NREC,
        NM_USUARIO_NREC,
        IE_SITUACAO,
        IE_LIBERAR_DESENV,
        DS_OBSERVACAO,
        NR_SEQ_REQUISITO)
    SELECT nextval('latam_historico_seq'), 
           DT_ATUAL_S, 
           NM_USUARIO_S, 
           DT_ATUALIZACAO_NREC, 
           NM_USUARIO_NREC, 
           IE_SITUACAO, 
           IE_LIBERAR_DESENV_P, 
           DS_OBSERVACAO_P,
           NR_SEQUENCIA_P 
    FROM   LATAM_REQUISITO 
    WHERE  NR_SEQUENCIA = NR_SEQUENCIA_P;

    UPDATE LATAM_REQUISITO
    SET    IE_LIBERAR_DESENV = IE_LIBERAR_DESENV_P,
           NM_USUARIO        = NM_USUARIO_S,
           DT_ATUALIZACAO    = DT_ATUAL_S
    WHERE  NR_SEQUENCIA      = NR_SEQUENCIA_P;

    COMMIT;
END;
