CREATE OR REPLACE PROCEDURE gerar_historico_reap ( ds_lista_convret_glosa_p text, nr_seq_conpaci_ret_hist_p bigint, nr_seq_hist_audit_p bigint, dt_historico_p timestamp, dt_reapresentacao_p timestamp, dt_recebimento_previsto_p timestamp, ds_observacao_p text, cd_motivo_glosa_p bigint, cd_resposta_glosa_p bigint, cd_processo_p bigint, vl_historico_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ret_glosa_w		bigint;
nr_seq_conpaci_ret_hist_novo_w	bigint;
vl_glosa_w			double precision;
vl_historico_w			double precision;
vl_historico_antigo_w		double precision;

c01 CURSOR FOR
SELECT	nr_sequencia,
	vl_glosa
from	convenio_retorno_glosa
where	' ' || ds_lista_convret_glosa_p || ' '  like '% ' || nr_sequencia || ' %';


BEGIN

if (coalesce(trim(both ds_lista_convret_glosa_p)::text, '') = '') then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(279963);
end if;

select	nextval('conta_paciente_ret_hist_seq')
into STRICT	nr_seq_conpaci_ret_hist_novo_w
;

insert into conta_paciente_ret_hist(nr_sequencia,
	dt_historico,
	vl_historico,
	dt_atualizacao,
	nm_usuario,
	nr_seq_conpaci_ret,
	nr_seq_hist_audit,
	nr_seq_ret_item,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	dt_reapresentacao,
	dt_receb_prev,
	ds_observacao,
	cd_motivo_glosa,
	cd_resposta,
	cd_processo_receb)
SELECT	nr_seq_conpaci_ret_hist_novo_w,
	dt_historico_p,
	coalesce(vl_historico_p,0),
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_conpaci_ret,
	nr_seq_hist_audit_p,
	nr_seq_ret_item,
	clock_timestamp(),
	nm_usuario_p,
	dt_reapresentacao_p,
	dt_recebimento_previsto_p,
	ds_observacao_p,
	cd_motivo_glosa_p,
	cd_resposta_glosa_p,
	cd_processo_p
from	conta_paciente_ret_hist
where	nr_sequencia	= nr_seq_conpaci_ret_hist_p;

open c01;
loop
fetch c01 into
	nr_seq_ret_glosa_w,
	vl_glosa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	CALL Desdobrar_item_glosado(nr_seq_ret_glosa_w, vl_glosa_w, 0, 1, nm_usuario_p, nr_seq_conpaci_ret_hist_novo_w);
end loop;
close c01;

/* Acertar histórico antigo */

select	coalesce(sum(vl_glosa),0)
into STRICT	vl_historico_w
from	convenio_retorno_glosa
where	nr_seq_conpaci_ret_hist = nr_seq_conpaci_ret_hist_p;

if (vl_historico_p IS NOT NULL AND vl_historico_p::text <> '') then
	select	max(coalesce(vl_historico,0)) - coalesce(vl_historico_p,0)
	into STRICT	vl_historico_w
	from	conta_paciente_ret_hist
	where	nr_sequencia	= nr_seq_conpaci_ret_hist_p;
end if;

update	conta_paciente_ret_hist
set	vl_historico 		= vl_historico_w
where	nr_sequencia		= nr_seq_conpaci_ret_hist_p;

/* Acertar histórico novo */

select	coalesce(sum(vl_glosa),0)
into STRICT	vl_historico_w
from	convenio_retorno_glosa
where	nr_seq_conpaci_ret_hist	= nr_seq_conpaci_ret_hist_novo_w;

update	conta_paciente_ret_hist
set	vl_historico 		= coalesce(vl_historico_p, vl_historico_w)
where	nr_sequencia		= nr_seq_conpaci_ret_hist_novo_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
