CREATE OR REPLACE PROCEDURE gerar_hist_ativ_os_etapa_cron ( nr_seq_ativ_os_p bigint, nr_seq_ativ_proj_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_obs_ativ_w	varchar(4000);


BEGIN
if (nr_seq_ativ_os_p IS NOT NULL AND nr_seq_ativ_os_p::text <> '') and (nr_seq_ativ_proj_p IS NOT NULL AND nr_seq_ativ_proj_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	max(ds_observacao)
	into STRICT	ds_obs_ativ_w
	from	man_ordem_serv_ativ
	where	nr_sequencia = nr_seq_ativ_os_p;

	if (ds_obs_ativ_w IS NOT NULL AND ds_obs_ativ_w::text <> '') then
		begin
		insert into proj_cron_etapa_hist(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_etapa_cron,
			dt_historico,
			ds_titulo,
			ds_historico,
			dt_liberacao)
		values (
			nextval('proj_cron_etapa_hist_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_ativ_proj_p,
			clock_timestamp(),
			substr(wheb_mensagem_pck.get_texto(305668),1,80),
			ds_obs_ativ_w,
			clock_timestamp());
		end;
	end if;
	end;
end if;
commit;
end;
$body$
