CREATE OR REPLACE PROCEDURE gerar_hist_audit_retorno ( nr_seq_retorno_p bigint, nr_seq_hist_audit_p bigint, dt_reapresentacao_p timestamp, dt_receb_prev_p timestamp, nm_usuario_p text, cd_motivo_glosa_p bigint, vl_glosa_p bigint, ie_filtrar_valor_p text, nr_seq_ret_item_p bigint) AS $body$
DECLARE


nr_seq_ret_glosa_w		bigint;
nr_seq_ret_item_w		bigint;
nr_seq_conpaci_ret_hist_w	bigint;

c01 CURSOR FOR
SELECT	distinct
	a.nr_seq_ret_item
from	motivo_glosa c,
	convenio_retorno_item b,
	convenio_retorno_glosa a
where	a.nr_seq_ret_item		= b.nr_sequencia
and	a.cd_motivo_glosa		= c.cd_motivo_glosa
and	coalesce(a.ie_acao_glosa,c.ie_acao_glosa) = 'R'
and	b.nr_seq_retorno		= nr_seq_retorno_p
and	b.nr_sequencia			= coalesce(nr_seq_ret_item_p,b.nr_sequencia)
and (coalesce(cd_motivo_glosa_p,0) = 0 or a.cd_motivo_glosa <> cd_motivo_glosa_p)
and (coalesce(ie_filtrar_valor_p,'N') = 'N' or a.vl_glosa <= vl_glosa_p);

c02 CURSOR FOR
SELECT	a.nr_sequencia
from	motivo_glosa b,
	convenio_retorno_glosa a
where	a.cd_motivo_glosa	= b.cd_motivo_glosa
and	coalesce(a.ie_acao_glosa,b.ie_acao_glosa) = 'R'
and	a.nr_seq_ret_item	= nr_seq_ret_item_w
and (coalesce(cd_motivo_glosa_p,0) = 0 or a.cd_motivo_glosa <> cd_motivo_glosa_p)
and (coalesce(ie_filtrar_valor_p,'N') = 'N' or a.vl_glosa <= vl_glosa_p);


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_ret_item_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	nr_seq_conpaci_ret_hist_w	:= null;
	nr_seq_ret_glosa_w		:= null;

	open c02;
	loop
	fetch c02 into
		nr_seq_ret_glosa_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		nr_seq_conpaci_ret_hist_w := gerar_hist_audit_ret_glosa(nr_seq_ret_item_w, nr_seq_ret_glosa_w, nr_seq_hist_audit_p, dt_reapresentacao_p, dt_receb_prev_p, nm_usuario_p, 'I', nr_seq_conpaci_ret_hist_w);
	end loop;
	close c02;


	nr_seq_conpaci_ret_hist_w := gerar_hist_audit_ret_glosa(nr_seq_ret_item_w, nr_seq_ret_glosa_w, nr_seq_hist_audit_p, dt_reapresentacao_p, dt_receb_prev_p, nm_usuario_p, 'F', nr_seq_conpaci_ret_hist_w);
end loop;
close c01;

commit;

end;
$body$
