CREATE OR REPLACE PROCEDURE gerar_horarios_plt_sessoes ( dt_inicial_p timestamp, dt_final_p timestamp, ie_prescr_usuario_p text, ie_exibe_sae_p text, cd_pessoa_fisica_p text, cd_setor_usuario_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_exibir_hor_suspensos_p text) AS $body$
DECLARE

 
nr_atendimento_w	bigint;

c01 CURSOR FOR 
	SELECT	nr_atendimento 
	from	atendimento_paciente 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;


BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	open c01;
		loop 
			fetch c01 into	 
				nr_atendimento_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
			begin 
			CALL gerar_horarios_plt_sessao( 
				cd_estabelecimento_p, 
				cd_setor_usuario_p, 
				cd_perfil_p, 
				nm_usuario_p, 
				nr_atendimento_w, 
				dt_inicial_p, 
				dt_final_p, 
				ie_prescr_usuario_p, 
				ie_exibe_sae_p, 
				'N', 
				'N', 
				'N', 
				2, 
				clock_timestamp(), 
				ie_exibir_hor_suspensos_p, 
				to_char(clock_timestamp(),'dd/mm/yyyy'), 
				cd_pessoa_fisica_p, 
				0);
			end;
		end loop;
	close c01;	
	end;
end if;
 
commit;
end;
$body$
