CREATE OR REPLACE PROCEDURE gerar_horario_agenda_consulta ( nm_usuario_p text, cd_estabelecimento_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp, dt_final_agenda_p timestamp, cd_agendas_usuario_p text, ie_forma_apresentacao_p bigint) AS $body$
DECLARE

		
ie_feriado_w		varchar(1);
ie_sobra_horario_w	varchar(1);
ds_horarios_w		varchar(255);
cd_agendas_usuario_w	varchar(4000);
qt_tam_lista_w		bigint;
nr_pos_virgula_w	smallint;
cd_agenda_w		bigint;
dt_agenda_w		timestamp;
dt_final_agenda_w	timestamp;
		

BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (ie_forma_apresentacao_p IS NOT NULL AND ie_forma_apresentacao_p::text <> '') then
	begin
	if (ie_forma_apresentacao_p = 0) and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') then
		begin
		select	coalesce(ie_feriado,'N'),
			coalesce(ie_gerar_sobra_horario,'N')
		into STRICT	ie_feriado_w,
			ie_sobra_horario_w
		from	agenda
		where	cd_agenda = cd_agenda_p;
		
		ds_horarios_w := horario_livre_consulta(cd_estabelecimento_p, cd_agenda_p, ie_feriado_w, dt_agenda_p, nm_usuario_p, 'S', ie_sobra_horario_w, 'N', 0, ds_horarios_w);
		end;
	elsif (ie_forma_apresentacao_p = 1) and (cd_agendas_usuario_p IS NOT NULL AND cd_agendas_usuario_p::text <> '') then
		begin
		cd_agendas_usuario_w	:= cd_agendas_usuario_p;		
		while(cd_agendas_usuario_w IS NOT NULL AND cd_agendas_usuario_w::text <> '') loop
			begin
			qt_tam_lista_w		:= length(cd_agendas_usuario_w);
			nr_pos_virgula_w	:= position(',' in cd_agendas_usuario_w);

			if (nr_pos_virgula_w <> 0) then
				begin
				cd_agenda_w		:= (substr(cd_agendas_usuario_w,1,(nr_pos_virgula_w - 1)))::numeric;
				cd_agendas_usuario_w	:= substr(cd_agendas_usuario_w,(nr_pos_virgula_w + 1),qt_tam_lista_w);
				end;
			else
				begin
				cd_agenda_w		:= somente_numero(cd_agendas_usuario_w);
				cd_agendas_usuario_w	:= null;
				end;
			end if;
			
			if (coalesce(cd_agenda_w,0) > 0) then
				begin
				select	coalesce(ie_feriado,'N'),
					coalesce(ie_gerar_sobra_horario,'N')
				into STRICT	ie_feriado_w,
					ie_sobra_horario_w
				from	agenda
				where	cd_agenda = cd_agenda_w;
				
				ds_horarios_w := horario_livre_consulta(cd_estabelecimento_p, cd_agenda_w, ie_feriado_w, dt_agenda_p, nm_usuario_p, 'S', ie_sobra_horario_w, 'N', 0, ds_horarios_w);
				end;
			end if;
			end;
		end loop;
		end;
	elsif (ie_forma_apresentacao_p in (2,3)) and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_final_agenda_p IS NOT NULL AND dt_final_agenda_p::text <> '') then
		begin
		select	coalesce(ie_feriado,'N'),
			coalesce(ie_gerar_sobra_horario,'N')
		into STRICT	ie_feriado_w,
			ie_sobra_horario_w
		from	agenda
		where	cd_agenda = cd_agenda_p;		
		
		dt_agenda_w		:= trunc(dt_agenda_p,'dd');
		dt_final_agenda_w	:= trunc(dt_final_agenda_p,'dd');		
		while(dt_agenda_w <= dt_final_agenda_w) loop
			begin
			ds_horarios_w := horario_livre_consulta(cd_estabelecimento_p, cd_agenda_p, ie_feriado_w, dt_agenda_w, nm_usuario_p, 'S', ie_sobra_horario_w, 'N', 0, ds_horarios_w);
			dt_agenda_w	:= dt_agenda_w + 1;
			end;
		end loop;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
