CREATE OR REPLACE PROCEDURE gerar_horario_agenda_serv_list ( cd_agenda_lista_p text, dt_agenda_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

		
cd_agenda_lista_w	varchar(4000);
cd_agenda_w	bigint;

c01 CURSOR FOR
	SELECT cd_agenda
	from w_agendas_liberadas
	where cd_tipo_agenda = 5
	and nm_usuario = nm_usuario_p;

BEGIN

	if (cd_agenda_lista_p IS NOT NULL AND cd_agenda_lista_p::text <> '') then
	
		cd_agenda_lista_w	:= cd_agenda_lista_p;
		while (cd_agenda_lista_w IS NOT NULL AND cd_agenda_lista_w::text <> '') loop
			begin
			cd_agenda_w	:= substr(cd_agenda_lista_w, 1, position(','  cd_agenda_lista_w) - 1);
			cd_agenda_lista_w	:= substr(cd_agenda_lista_w, position(',' in cd_agenda_lista_w) + 1, length(cd_agenda_lista_w));
			
			CALL gerar_horario_agenda_servico(
				cd_estabelecimento_p,
				cd_agenda_w,
				dt_agenda_p,
				nm_usuario_p);
			end;
		end loop;
	
	
	else

		for c_01 in c01 loop

			CALL gerar_horario_agenda_servico(
					cd_estabelecimento_p,
					c_01.cd_agenda,
					dt_agenda_p,
					nm_usuario_p);

		end loop;
		
	end if;

end;
