SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_horario_inicial_etapa (nr_seq_etapa_cron_p bigint, ie_acao_executada_p text) AS $body$
DECLARE


qt_etapa_w	bigint;
dt_inicio_w	timestamp;


BEGIN
if (nr_seq_etapa_cron_p IS NOT NULL AND nr_seq_etapa_cron_p::text <> '') then
	select	COUNT(*)
	into STRICT	qt_etapa_w
	from	proj_rat_ativ a
	where	a.nr_seq_etapa_cron = nr_seq_etapa_cron_p;

	if	(qt_etapa_w = 1 AND ie_acao_executada_p = 'I') then
		select	a.dt_inicio_ativ
		into STRICT	dt_inicio_w
		from	proj_rat_ativ a
		where	a.nr_seq_etapa_cron = nr_seq_etapa_cron_p;

		update	proj_cron_etapa
		set	dt_inicio_real	= dt_inicio_w
		where	nr_sequencia		= nr_seq_etapa_cron_p;

	elsif	(qt_etapa_w = 1 AND ie_acao_executada_p = 'E') then
		update	proj_cron_etapa
		set	dt_inicio_real	 = NULL
		where	nr_sequencia		= nr_seq_etapa_cron_p;
	end if;

	CALL Proj_Atualiza_inicio_cron(nr_seq_etapa_cron_p);
end if;

COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
