CREATE OR REPLACE PROCEDURE gerar_horario_ordem ( nr_seq_ordem_p bigint, dt_horario_p timestamp, nm_usuario_p text, ie_horario_especial_p text) AS $body$
DECLARE


nr_seq_w	bigint;


BEGIN

select	nextval('prescr_ordem_hor_seq')
into STRICT	nr_seq_w
;

insert into prescr_ordem_hor(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_ordem,
	dt_horario,
	ie_horario_especial)
values (nr_seq_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_ordem_p,
	dt_horario_p,
	'N');

if (coalesce(ie_horario_especial_p, 'N') = 'S') then

	select	nextval('prescr_ordem_hor_seq')
	into STRICT	nr_seq_w
	;

	insert into prescr_ordem_hor(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_ordem,
		dt_horario,
		ie_horario_especial)
	values (nr_seq_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_ordem_p,
		dt_horario_p + 1,
		ie_horario_especial_p);
end if;

commit;

end;
$body$
