CREATE OR REPLACE PROCEDURE gerar_hor_cronograma (nr_seq_cronograma_p bigint) AS $body$
DECLARE


qt_hora_adic_w		double precision := 0;
ie_atividade_extra_w	varchar(2);
qt_min_ativ_w		bigint;
nr_sequencia_w		bigint;
qt_total_hora_adic_w	double precision;


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	PROJ_CRON_ETAPA
	where	nr_seq_cronograma = nr_seq_cronograma_p
	and	ie_modulo = 'N'
	order by 1;

C02 CURSOR FOR
	SELECT	qt_min_ativ,
		ie_atividade_extra
	from	proj_rat_ativ
	where	nr_seq_etapa_cron = nr_sequencia_w;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL atualizar_horas_etapa_cron(nr_sequencia_w);

	qt_total_hora_adic_w := 0;
	open C02;
	loop
	fetch C02 into
		qt_min_ativ_w,
		ie_atividade_extra_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
	qt_hora_adic_w := 0;

	if (ie_atividade_extra_w = 'S') then
		qt_hora_adic_w := coalesce(round((dividir(qt_min_ativ_w,60))::numeric,2),0);
	end if;

	qt_total_hora_adic_w := qt_total_hora_adic_w + qt_hora_adic_w;

	update	proj_cron_etapa
	set	qt_horas_etapa_adic = qt_total_hora_adic_w
	where	nr_sequencia = nr_sequencia_w;

	end;
	end loop;
	close C02;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
