CREATE OR REPLACE PROCEDURE gerar_imagem_legenda_idioma ( cd_funcao_p bigint, nr_seq_idioma_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_objeto_w		bigint;
ds_texto_w		varchar(255);
cd_funcao_w		integer;
nr_seq_traducao_w	bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.ds_legenda,
	b.cd_funcao
from	tasy_legenda b,
	tasy_padrao_imagem a
where	b.nr_sequencia = a.nr_seq_legenda
and	b.cd_funcao = cd_funcao_p
and	(a.ds_legenda IS NOT NULL AND a.ds_legenda::text <> '')
and	not exists (
		SELECT	1
		from	tasy_padrao_imagem_idioma x
		where	x.nr_seq_imagem = a.nr_sequencia
		and	x.nr_seq_idioma = nr_seq_idioma_p)
order by
	a.nr_sequencia;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nr_seq_idioma_p IS NOT NULL AND nr_seq_idioma_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into	nr_seq_objeto_w,
			ds_texto_w,
			cd_funcao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if ((trim(both ds_texto_w) IS NOT NULL AND (trim(both ds_texto_w))::text <> '')) then
			begin
			select	nextval('tasy_padrao_imagem_idioma_seq')
			into STRICT	nr_seq_traducao_w
			;

			insert into tasy_padrao_imagem_idioma(
				nr_sequencia,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_atualizacao,
				nm_usuario,
				nr_seq_imagem,
				ds_descricao,
				cd_funcao,
				nr_seq_idioma,
				ds_traducao,
				ie_necessita_revisao)
			values (
				nr_seq_traducao_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_objeto_w,
				ds_texto_w,
				cd_funcao_w,
				nr_seq_idioma_p,
				' ',
				'T');
			end;
		end if;
		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
