CREATE OR REPLACE PROCEDURE gerar_inconsist_tit_escrit ( nr_seq_tit_escrit_p bigint, nr_seq_inconsistencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar as inconsistências para o título em cobrança.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_inconsistencia_w			bigint;


BEGIN
select	count(1)
into STRICT	qt_inconsistencia_w
from	inconsistencia_tit_escrit	a
where	a.nr_seq_titulo_escrit	= nr_seq_tit_escrit_p
and	nr_seq_inconsistencia	= nr_seq_inconsistencia_p  LIMIT 1;

if (qt_inconsistencia_w = 0) then
	insert into inconsistencia_tit_escrit(nr_sequencia,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		ds_motivo_liberacao,
		nr_seq_inconsistencia,
		dt_liberacao,
		nr_seq_titulo_escrit)
	values (nextval('inconsistencia_tit_escrit_seq'),
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		null,
		nr_seq_inconsistencia_p,
		null,
		nr_seq_tit_escrit_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
