CREATE OR REPLACE PROCEDURE gerar_indicacao_albu_rotina ( nr_seq_indicacao_p bigint, nm_usuario_p text, nr_seq_prescr_albu_p bigint, ds_observacao_p text) AS $body$
DECLARE

nr_seq_grupo_w	bigint;
nr_sequencia_w	bigint;
ie_obriga_obs_w	varchar(1);

BEGIN
if (nr_seq_prescr_albu_p IS NOT NULL AND nr_seq_prescr_albu_p::text <> '') and (nr_seq_indicacao_p IS NOT NULL AND nr_seq_indicacao_p::text <> '') then

	select	max(nr_seq_grupo)
	into STRICT	nr_seq_grupo_w
	from	indicacao_albumina
	where	nr_sequencia = nr_seq_indicacao_p;

	select	nextval('prescr_medica_albu_indic_seq')
	into STRICT	nr_sequencia_w
	;

	if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') and (coalesce(ds_observacao_p::text, '') = '') then

		select	coalesce(max(ie_obriga_obs),'N')
		into STRICT	ie_obriga_obs_w
		from	grupo_indicacao_albumina
		where	nr_sequencia = nr_seq_grupo_w;

		if (ie_obriga_obs_w = 'S') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(48111);
		end if;

	end if;

	insert into prescr_medica_albu_indic(
		ds_observacao,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_grupo,
		nr_seq_indicacao,
		nr_seq_prescr_albu,
		nr_sequencia)
	values (	ds_observacao_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_grupo_w,
		nr_seq_indicacao_p,
		nr_seq_prescr_albu_p,
		nr_sequencia_w);
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
