SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_indicador_evolucao (cd_evolucao_p bigint, ie_acao_p text, ie_tipo_evolucao_p text, nm_usuario_p text) AS $body$
DECLARE



c01 CURSOR FOR
	SELECT	cd_indicador_evolucao
	from	indicador_evolucao
	where	ie_tipo_evolucao = ie_tipo_evolucao_p
	and	ie_situacao = 'A';


/* 	'G' - Gerar
	'C' - Copiar */
cd_pessoa_fisica_w		varchar(10);
cd_indicador_evolucao_w		integer;
nr_sequencia_w			bigint;


BEGIN

delete	from indicador_evolucao_paciente
where	cd_evolucao	= cd_evolucao_p;


select	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	evolucao_paciente
where	cd_evolucao	= cd_evolucao_p;


if (ie_acao_p	= 'G') then
	begin

	open	c01;
	loop
	fetch	c01 into cd_indicador_evolucao_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	coalesce(max(nr_sequencia),0) + 1
		into STRICT	nr_sequencia_w
		from	indicador_evolucao_paciente
		where	cd_evolucao	= cd_evolucao_p;

		insert into indicador_evolucao_paciente(cd_evolucao,
			nr_sequencia,
			cd_indicador_evolucao,
			dt_atualizacao,
			nm_usuario,
			vl_informado,
			vl_padrao)
		values (cd_evolucao_p,
			nr_sequencia_w,
			cd_indicador_evolucao_w,
        		clock_timestamp(),
			nm_usuario_p,
			null,
			null);
		end;
	end loop;
	close c01;
	end;
elsif (ie_acao_p	= 'C') then
	insert into indicador_evolucao_paciente(cd_evolucao,
		nr_sequencia,
		cd_indicador_evolucao,
		dt_atualizacao,
		nm_usuario,
		vl_informado,
		vl_padrao)
	(SELECT  cd_evolucao_p,
		nr_sequencia,
		cd_indicador_evolucao,
		clock_timestamp(),
		nm_usuario_p,
		vl_informado,
		vl_padrao
	from	indicador_evolucao_paciente a
	where	cd_evolucao =
		(select max(b.cd_evolucao)
		from 	evolucao_paciente b
		where	b.cd_evolucao <> cd_evolucao_p
		and 	b.cd_pessoa_fisica = cd_pessoa_fisica_w
       		and	b.ie_tipo_evolucao = ie_tipo_evolucao_p));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
