CREATE OR REPLACE PROCEDURE gerar_indice_fk_faltante ( nm_usuario_p text) AS $body$
DECLARE


nm_tabela_w			varchar(50);
nm_integr_ref_w		varchar(50);
ds_erro_w			varchar(2000);

C01 CURSOR FOR
	SELECT
		nm_tabela,
		nm_integridade_referencial
	from	integridade_referencial
	WHERE coalesce(obter_indice_constraint(null, nm_tabela, nm_integridade_referencial, 'D')::text, '') = ''
	order by 1,2;



BEGIN


OPEN C01;
LOOP
FETCH C01 into
	nm_tabela_w,
	nm_integr_ref_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL Gerar_Indice_FK(nm_tabela_w, nm_integr_ref_w, 'Tasy');
	exception
		when others then
			ds_erro_w	:= SQLERRM(SQLSTATE);
			--insert into log_xxxtasy values(sysdate, nm_usuario_p, 778, 'Tabela: ' || nm_tabela_w || ' IF: ' ||
	---			nm_integr_ref_w || ' Erro: ' || ds_erro_w);
	end;
END LOOP;
Close C01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
