CREATE OR REPLACE PROCEDURE gerar_info_torniquete (nr_sequencia_p bigint, nr_seq_topografia_p bigint, nr_seq_tipo_clamp_p bigint, qt_pressao_p bigint, ie_altera_pressao_p text default 'N') AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	if (ie_altera_pressao_p = 'S') then
		
		update 	evento_cirurgia_paciente
		set 	qt_pressao = qt_pressao_p
		where 	nr_sequencia = nr_sequencia_p;
	
	else	
		update 	evento_cirurgia_paciente
		set 	nr_seq_topografia = nr_seq_topografia_p,
				nr_seq_tipo_clamp = nr_seq_tipo_clamp_p,
				qt_pressao = qt_pressao_p
		where 	nr_sequencia = nr_sequencia_p;
	end if;
	
	commit;
end if;

end;
$body$
