CREATE OR REPLACE PROCEDURE gerar_intercorrencia_js ( nr_seq_atendimento_p bigint, dt_intercorrencia_p timestamp, ds_motivo_p text, nr_seq_tipo_interc_p bigint, ie_evolucao_interc_p text, ie_opcao_p text, dt_fim_intercorrencia_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
/*	ie_opcao_p 
	G - Gerar 
	F - Finalizar */
 
 
ie_enviar_ci_w	varchar(1);
nr_sequencia_w	bigint;

 

BEGIN 
 
if (ie_opcao_p = 'G') then 
	begin 
	nr_sequencia_w := gerar_intercorrencia( 
		nr_seq_atendimento_p, dt_intercorrencia_p, ds_motivo_p, nm_usuario_p, nr_seq_tipo_interc_p, nr_sequencia_w);
 
	if (ie_evolucao_interc_p = 'S') then 
		nr_sequencia_w := gerar_evolucao_intercorrencia(nr_seq_atendimento_p, nm_usuario_p, nr_sequencia_w);
	end if;	
	end;
 
elsif (ie_opcao_p = 'F') then 
	begin 
	update	paciente_atend_interc 
	set	dt_fim_intercorrencia	= coalesce(dt_fim_intercorrencia_p,clock_timestamp()), 
		nm_usuario		= nm_usuario_p 
	where	nr_seq_atendimento		= nr_seq_atendimento_p 
	and	coalesce(dt_fim_intercorrencia::text, '') = '';
 
	/* Quimioterapia - Parâmetro [12] - Enviar comunicação interna com aviso de intercorrência */
 
	ie_enviar_ci_w := obter_param_usuario(3130, 12, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_enviar_ci_w);
 
	if (ie_enviar_ci_w = 'S') then 
		CALL comunicar_intercorrencia(nr_seq_atendimento_p, nm_usuario_p);
	end if;
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
