CREATE OR REPLACE PROCEDURE gerar_intercorrencia ( nr_seq_atendimento_p bigint, dt_intercorrencia_p timestamp, ds_motivo_p text, nm_usuario_p text, nr_seq_tipo_interc_p bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


ds_motivo_w	varchar(4000);
nr_sequencia_w	bigint;


BEGIN
ds_motivo_w	:= substr(ds_motivo_p,1,4000);

select	nextval('paciente_atend_interc_seq')
into STRICT	nr_sequencia_w
;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	insert into paciente_atend_interc(
		nr_sequencia,
		nr_seq_atendimento,
		dt_atualizacao,
		nm_usuario,
        nm_usuario_nrec,
		dt_intercorrencia,
		dt_fim_intercorrencia,
		ds_motivo,
		nr_seq_tipo_interc,
        ie_situacao)
	values (nr_sequencia_w,
		nr_seq_atendimento_p,
		clock_timestamp(),
		nm_usuario_p,
        nm_usuario_p,
		dt_intercorrencia_p,
		null,
		ds_motivo_w,
		nr_seq_tipo_interc_p,
        'A'  );

	commit;

	nr_sequencia_p := nr_sequencia_w;
end if;

end;
$body$
